SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW san_regra_brinde_v (cd_brinde, cd_material, ds_brinde, nr_seq_tipo_doacao, ds_tipo_doacao, qt_doacao) AS select	x.nr_sequencia cd_brinde,
	x.cd_material cd_material,
	substr(obter_desc_material(cd_material),1,255) ds_brinde,
	x.nr_seq_tipo_doacao nr_seq_tipo_doacao,
	substr(obter_desc_tipo_doacao(x.nr_seq_tipo_doacao),1,100) ds_tipo_doacao,
	x.qt_doacao qt_doacao
FROM	san_regra_brinde x
where	x.ie_situacao	= 'A'
order by	3;

