		when a.dt_periodo_final < a.dt_alta then 4
		else
		CASE WHEN a.ie_tipo_atendimento=1 THEN 			CASE WHEN a.cd_motivo_alta=1 THEN 1 WHEN a.cd_motivo_alta=2 THEN 1 WHEN a.cd_motivo_alta=4 THEN 1 WHEN a.cd_motivo_alta=6 THEN 1 WHEN a.cd_motivo_alta=7 THEN 1 WHEN a.cd_motivo_alta=8 THEN 1 WHEN a.cd_motivo_alta=10 THEN 4 WHEN a.cd_motivo_alta=11 THEN 4 WHEN a.cd_motivo_alta=12 THEN 4 WHEN a.cd_motivo_alta=13 THEN 4 WHEN a.cd_motivo_alta=14 THEN 4 WHEN a.cd_motivo_alta=15 THEN 4 WHEN a.cd_motivo_alta=16 THEN 4 WHEN a.cd_motivo_alta=17 THEN 4 WHEN a.cd_motivo_alta=18 THEN 3 WHEN a.cd_motivo_alta=27 THEN 2 WHEN a.cd_motivo_alta=28 THEN 2 WHEN a.cd_motivo_alta=29 THEN 2 WHEN a.cd_motivo_alta=30 THEN 2 WHEN a.cd_motivo_alta=31 THEN 2 WHEN a.cd_motivo_alta=32 THEN 2 WHEN a.cd_motivo_alta=33 THEN 2 WHEN a.cd_motivo_alta=34 THEN 2 WHEN a.cd_motivo_alta=44 THEN 1 WHEN a.cd_motivo_alta=45 THEN 1 WHEN a.cd_motivo_alta=46 THEN 1 WHEN a.cd_motivo_alta=47 THEN 1 WHEN a.cd_motivo_alta=48 THEN 1  ELSE 4 END   ELSE 4 END 	
	end cd_motivo_cobranca,
	a.dt_periodo_inicial			dt_entrada,
	a.dt_periodo_final			dt_alta,
	substr(a.nr_doc_convenio,1,6)	nr_guia,
	coalesce((substr(lpad(c.nr_seq_conta_convenio,10,'0'),6,5))::numeric ,
	(substr(lpad(c.nr_interno_conta,10,'0'),6,5))::numeric ) nr_interno_conta,
	0				vl_total_conta,
	0				nr_folha_ini_despesa,
	0				nr_folha_fim_despesa,
	0				vl_total_despesa,
	0				nr_folha_ini_servico,
	0				nr_folha_fim_servico,
	0				vl_total_servico,
	0				vl_total_honorario,
	0				nr_linha,
	LOCALTIMESTAMP				dt_item,
	0				cd_item_convenio,
	0				qt_dias_executado,
	0				qt_ocorrencia_dia,
	0				cd_funcao_executor,
	0				ie_via_acesso,
	0				ie_urgencia,
	0				vl_honorario,
	sum(c.vl_total_item)		vl_total_item,
	sum(CASE WHEN c.cd_item_convenio=8001 THEN c.vl_total_item WHEN c.cd_item_convenio=32200005 THEN c.vl_total_item  ELSE 0 END )
					vl_total_matmed,
	' '				cd_brasindice,
	' '				cd_unidade_medida
from	w_interf_conta_item_ipe c,
	w_interf_conta_total b,
	w_interf_conta_cab a
where	a.nr_interno_conta		= b.nr_interno_conta
and	a.nr_interno_conta		= c.nr_interno_conta
and	c.tp_nota			in (77,87)
group by
	a.nr_seq_protocolo,
	CASE WHEN a.ie_tipo_atendimento=1 THEN 77 WHEN a.ie_tipo_atendimento=8 THEN 87  ELSE 0 END ,
	c.nr_folha,
	substr(a.cd_usuario_convenio,1,13),
	substr(a.cd_cid_principal,1,6),
	a.cd_interno,
	case
		when a.dt_periodo_final < a.dt_alta then 4
		else
		CASE WHEN a.ie_tipo_atendimento=1 THEN 			CASE WHEN a.cd_motivo_alta=1 THEN 1 WHEN a.cd_motivo_alta=2 THEN 1 WHEN a.cd_motivo_alta=4 THEN 1 WHEN a.cd_motivo_alta=6 THEN 1 WHEN a.cd_motivo_alta=7 THEN 1 WHEN a.cd_motivo_alta=8 THEN 1 WHEN a.cd_motivo_alta=10 THEN 4 WHEN a.cd_motivo_alta=11 THEN 4 WHEN a.cd_motivo_alta=12 THEN 4 WHEN a.cd_motivo_alta=13 THEN 4 WHEN a.cd_motivo_alta=14 THEN 4 WHEN a.cd_motivo_alta=15 THEN 4 WHEN a.cd_motivo_alta=16 THEN 4 WHEN a.cd_motivo_alta=17 THEN 4 WHEN a.cd_motivo_alta=18 THEN 3 WHEN a.cd_motivo_alta=27 THEN 2 WHEN a.cd_motivo_alta=28 THEN 2 WHEN a.cd_motivo_alta=29 THEN 2 WHEN a.cd_motivo_alta=30 THEN 2 WHEN a.cd_motivo_alta=31 THEN 2 WHEN a.cd_motivo_alta=32 THEN 2 WHEN a.cd_motivo_alta=33 THEN 2 WHEN a.cd_motivo_alta=34 THEN 2 WHEN a.cd_motivo_alta=44 THEN 1 WHEN a.cd_motivo_alta=45 THEN 1 WHEN a.cd_motivo_alta=46 THEN 1 WHEN a.cd_motivo_alta=47 THEN 1 WHEN a.cd_motivo_alta=48 THEN 1  ELSE 4 END   ELSE 4 END 	
	end,
	a.dt_periodo_inicial,
	a.dt_periodo_final,
	substr(a.nr_doc_convenio,1,6),
	coalesce((substr(lpad(c.nr_seq_conta_convenio,10,'0'),6,5))::numeric ,
	(substr(lpad(c.nr_interno_conta,10,'0'),6,5))::numeric )

union all

select
	7				tp_registro,
	c.nr_seq_protocolo		nr_seq_protocolo,
	'SMH'				nm_sistema,
	' '				cd_cgc_hospital,
	0				qt_total_conta,
	0 				qt_total_lancamento,
	0				cd_interno,
	' '				nm_hospital,
	' '				ds_espaco,
	0 				ie_zeros,
	c.tp_nota			tp_nota,
	c.nr_folha			nr_folha,
	' '				cd_usuario_convenio,
	' '				cd_cid,
	CASE WHEN c.ie_responsavel_credito='OPME' THEN c.cd_cgc_prestador WHEN c.ie_responsavel_credito='M' THEN CASE WHEN c.cd_medico_executor IS NULL THEN c.cd_cgc_prestador  ELSE obter_dados_pf(c.cd_medico_executor,'CPF') END  WHEN c.ie_responsavel_credito='MEDIC' THEN CASE WHEN c.cd_medico_executor IS NULL THEN c.cd_cgc_prestador  ELSE obter_dados_pf(c.cd_medico_executor,'CPF') END  WHEN c.ie_responsavel_credito='RM' THEN c.cd_cgc_hospital  ELSE c.ie_prestador END  cd_prestador,
	3				tp_prestador,
	0 				cd_motivo_cobranca,
	LOCALTIMESTAMP				dt_entrada,
	LOCALTIMESTAMP				dt_alta,
	' '				nr_guia,
	coalesce((substr(lpad(c.nr_seq_conta_convenio,10,'0'),6,5))::numeric ,
	(substr(lpad(c.nr_interno_conta,10,'0'),6,5))::numeric ) nr_interno_conta,
	0				vl_total_conta,
	0				nr_folha_ini_despesa,
	0				nr_folha_fim_despesa,
	0				vl_total_despesa,
	0				nr_folha_ini_servico,
	0				nr_folha_fim_servico,
	0				vl_total_servico,
	0 				vl_total_honorario,
	c.nr_linha			nr_linha,
	c.dt_item			dt_item,
	c.cd_item_convenio		cd_item_convenio,
	1				qt_dias_executado,
	c.qt_item			qt_ocorrencia_dia,
	c.cd_funcao_executor		cd_funcao_executor,	
	c.ie_via_acesso			ie_via_acesso,
	c.ie_urgencia			ie_urgencia,
	CASE WHEN c.vl_total_item=0 THEN c.vl_honorario  ELSE c.vl_total_item END 
					vl_honorario,
	c.vl_total_item			vl_total_item,
	0				vl_total_matmed,
	' '				cd_brasindice,
	' '				cd_unidade_medida
from	w_interf_conta_item_ipe c,
	w_interf_conta_cab a
where	c.tp_nota	in (77,87)
  and	a.nr_interno_conta		= c.nr_interno_conta;

