CREATE OR REPLACE VIEW sepsis_culture_v (nr_atendimento, nr_sequencia, fld_nm_first_col_content, fld_nm_second_col_sub_info, fld_nm_second_col_content, fld_nm_sub_content, fld_nm_chart_tooltip, fld_nm_chart_first_val, fld_nm_chart_seccond_val, fld_nm_third_col_content, nr_delta_view) AS select	c.nr_atendimento,
