SELECT 	13 						TP_REGISTRO, 
	0 						nr_linha, 
  	'13' 						CD_registro, 
  	0 						nr_seq_registro, 
	''						cd_cgc_cpf_prestador, 			 
	0						NR_RELACAO, 
  	a.nr_interno_conta				NR_INTERNO_CONTA, 
  	a.nr_seq_protocolo				NR_SEQ_PROTOCOLO, 
  	''						tp_documento, 
 	substr(x.nr_doc_convenio,1,8) 			nr_documento, 
	0						NR_SEQ_DOCUMENTO, 
	LOCALTIMESTAMP						DT_EMISSAO, 
	''						CD_USUARIO_CONVENIO, 
	0						cd_cgc_cpf_medico_solic, 
	''						IE_ORIGEM_DOC, 
	''						ie_versao_cid, 
	''						CD_CID, 
	LOCALTIMESTAMP						DT_INTERNACAO, 
	LOCALTIMESTAMP						DT_ALTA, 
	''						ie_carater_intern, 
	0						CD_MOTIVO_ALTA, 
	0						ie_clinica,  	 
  	Campo_numerico(a.cd_item_convenio) 		cd_procedimento, 
  	LOCALTIMESTAMP						DT_ENTRADA, 
  	LOCALTIMESTAMP						DT_SAIDA, 
  	CASE WHEN a.ie_tipo_item=1 THEN sum(a.qt_item)  ELSE 1 END  	qt_procedimento, 
  	sum(CASE WHEN a.cd_area_proc=1 THEN (a.vl_honorario + a.vl_custo_oper) WHEN a.cd_area_proc=4 THEN (a.vl_honorario + a.vl_custo_oper)  ELSE a.vl_total_item END ) 
							vl_procedimento, 
  	coalesce(a.nr_cpf_executor,a.cd_cgc_hospital)	cd_cgc_cpf_med_exec, 
  	somente_numero(coalesce(a.cd_cgc_cpf_resp_cred, a.cd_cgc_hospital)) 
							cd_cgc_cpf_resp_cred, 
  	CASE WHEN a.ie_tipo_item=1 THEN  a.dt_item  ELSE coalesce(x.dt_alta,x.dt_periodo_final) END  
							dt_procedimento, 
  	a.cd_funcao_executor 				cd_atividade_prestador, 
  	a.nr_porte_anestesico 				ie_porte_anestesico, 
  	CASE WHEN a.ie_tipo_item=1 THEN  a.dt_item  ELSE coalesce(x.dt_alta,x.dt_periodo_final) END  
							hr_atendimento, 
  	' '						ie_cirurgia_multipla, 
  	a.pr_funcao_participante * 100 			tx_procedimento, 
  	'N' 						ie_pagar_honorario, 
  	CASE WHEN a.cd_classif_setor='4' THEN 'S'  ELSE 'N' END  		ie_procedimento_uti, 
	0						nr_total_linha, 
	0						VL_TOTAL, 
	0						QT_DOCUMENTO, 
  	a.cd_item					CD_ITEM, 
	'00'						ie_tp_nasc, 
	'00000000000000000000000000000'			ds_zeros, 
  	'0000000000000000000000000' 			CAMPO_LIVRE,  	 
  	' ' 						nm_usuario_carteirinha, 
  	' ' 						ie_natureza_juridica, 
  	' ' 						ie_tipo_plano, 
  	10 						cd_campo_quebra, 
	' ' 						nr_cpf_solic, 
	'0'						TP_MOVIMENTO, 
	' '						NR_VERSAO, 
	'1'						ie_carater_atend, 
	' '						ie_tipo_atend_tiss, 
	' '						tp_saida_sadt, 
	' '						nr_guia_principal, 
	' '						ie_acidente, 
	' '						cd_cid_obito, 
	' '						cd_cid_secundario, 
	' '						cd_cid_terceiro, 
	' '						tp_doenca, 
	0						tempo_doenca, 
	' '						ds_reservado, 
	0						QT_NASC_VIVOS_TERMO, 
	0						QT_NASC_MORTOS, 
	0 						qt_NASC_VIVO_PREMATURO, 
	0 						qt_obito_precoce, 
	0						qt_obito_tardio, 
	' ' 						nr_declaracao_obito, 
	' ' 						ID_INT_OBSTETRICIA_1, 
	' ' 						ID_INT_OBSTETRICIA_2, 
	' ' 						ID_INT_OBSTETRICIA_3, 
	' ' 						ID_INT_OBSTETRICIA_4, 
	' ' 						ID_INT_OBSTETRICIA_5, 
	' ' 						ID_INT_OBSTETRICIA_6, 
	' ' 						ID_INT_OBSTETRICIA_7, 
	' ' 						ID_INT_OBSTETRICIA_8, 
	' ' 						ID_INT_OBSTETRICIA_9, 
	substr(OBTER_TECNICA_UTILIZADA_TISS(a.nr_seq_item),1,1) ie_video, 
	' '						ds_observacao, 
	' '						ds_indicacao_clinica 
from	w_interf_conta_cab 	x, 
	w_interf_conta_item 	a 
where	a.nr_interno_conta		= x.nr_interno_conta 
and	coalesce(a.ie_total_interf,0)	<> 5 
and	not(coalesce(a.cd_funcao_executor,0)	in (5,8)) 
group by Campo_numerico(a.cd_item_convenio), 
	 a.nr_interno_conta, 
	 a.nr_seq_protocolo, 
	 obter_cpf_pessoa_fisica(a.CD_MEDICO_REQ) , 
	 coalesce(a.nr_cpf_executor,a.cd_cgc_hospital), 
	 somente_numero(coalesce(a.cd_cgc_cpf_resp_cred, a.cd_cgc_hospital)), 
	 CASE WHEN a.ie_tipo_item=1 THEN  a.dt_item  ELSE coalesce(x.dt_alta,x.dt_periodo_final) END , 
	 a.cd_funcao_executor, 
	 a.nr_porte_anestesico, 
 	 a.pr_via_acesso, 
	 a.pr_funcao_participante, 
	 a.ie_emite_conta, 
	 a.cd_classif_setor, 
	 CASE WHEN a.ie_tipo_item=1 THEN  a.dt_item  ELSE coalesce(x.dt_alta,x.dt_periodo_final) END , 
	 a.cd_item, 
	 a.ie_tipo_item, 
	 substr(x.nr_doc_convenio,1,8), 
	substr(OBTER_TECNICA_UTILIZADA_TISS(a.nr_seq_item),1,1) 
having 	 sum(CASE WHEN a.ie_tipo_item=1 THEN  a.qt_item  ELSE a.vl_total_item END ) > 0 

