SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW softlab_procedimento_ws_v (numero_atendimento, sequencia_exame, codigo_exame, quantidade) AS SELECT	b.nr_prescricao numero_Atendimento,
	c.nr_sequencia sequencia_Exame,
	substr(e.cd_exame_equip,1,20) codigo_exame,
	c.qt_procedimento quantidade
FROM  	atendimento_paciente a,
	prescr_medica b,
	prescr_procedimento c,
	exame_laboratorio d,
	lab_exame_equip e,
	equipamento_lab f
WHERE	a.nr_atendimento = b.nr_atendimento
AND	b.nr_prescricao = c.nr_prescricao
AND	c.nr_seq_Exame = d.nr_seq_exame
AND	d.nr_seq_exame = e.nr_seq_exame
AND	e.cd_equipamento = f.cd_equipamento
AND	f.ds_sigla = 'SOFTLABWS';

