SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW status_lis_dominio_v (cd_dominio_lis, ds_dominio_lis) AS SELECT	(vl_dominio)::numeric  cd_dominio_lis,
	coalesce(ds_valor_dominio_cliente,
	coalesce((	SELECT	MAX(w.ds_status)
		FROM 	regra_exame_status w
		WHERE	w.ie_status_regra = vl_dominio
		AND	coalesce(w.ie_situacao, 'A') = 'A'),
	coalesce(obter_desc_expressao(cd_exp_valor_dominio, obter_regra_exame_status(vl_dominio)), ds_valor_dominio))) ds_dominio_lis
FROM	valor_dominio
WHERE	cd_dominio = 1030
AND 	coalesce(ie_situacao, 'A') = 'A'
ORDER BY 1;

