SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW surgery_figures_v (op_name, cd_agenda, hr_inicio, nr_minuto_duracao, actual_duration, establishment_name) AS SELECT
        upper(obter_nome_agenda(ap.cd_agenda)) op_name,
        ap.cd_agenda            cd_agenda,
        ap.hr_inicio            hr_inicio,
        c.nr_min_duracao_prev   nr_minuto_duracao,
        ap.nr_minuto_duracao    actual_duration,
        upper(obter_nome_estabelecimento(obter_estab_agenda(cd_agenda))) establishment_name
    FROM
        agenda_paciente   ap
        LEFT JOIN cirurgia c ON ap.nr_cirurgia = c.nr_cirurgia;

