CREATE OR REPLACE VIEW sus_interf_aih_hdp_v (ds_registro, tp_registro, nr_lote, nr_seq_laudo, nr_identificacao, cd_cgc, nr_laudos, ie_tipo_laudo, nr_laudo, nm_paciente, ie_sexo, dt_nascimento, nr_prontuario, ds_endereco, ds_numero, ds_complemento, ds_bairro, cd_municipio, ds_uf, nr_cep, dt_internacao, dt_alta, ie_tipo_alta, nr_cpf_solic, cd_procedimento_solic, cd_carater_internacao, ie_clinica, nr_cartao_sus, ie_motivo_cobranca, nr_aih, nr_sequencia, ie_tipo_aih, nr_gestante_prenatal, dt_apresentacao, nr_cerih, cd_cid_principal, nr_cpf, nr_cmce, nr_cnrac, nr_cns_sol, cd_proced_solic, qt_procedimento, dt_competencia, nr_serie) AS select	'Cabecalho' ds_registro,
	1 tp_registro, 
	c.nr_sequencia nr_lote, 
	0 nr_seq_laudo, 
	'24' nr_identificacao, 
	substr(obter_cgc_estabelecimento(c.cd_estabelecimento),1,14) cd_cgc, 
	adiciona_zeros_esquerda((select 	count(*) 
				FROM 	sus_laudo_paciente x 
				where 	x.nr_seq_lote = c.nr_sequencia),4) nr_laudos, 
	'' ie_tipo_laudo, 
	null nr_laudo, 
