	null ie_sexo, 
	null dt_nascimento, 
	null nr_prontuario, 
	null ds_endereco, 
	null ds_numero, 
	null ds_complemento, 
	null ds_bairro, 
	null cd_municipio, 
	null ds_uf, 
	null nr_cep, 
	null dt_internacao, 
	null dt_alta, 
	null ie_tipo_alta, 
	null nr_cpf_solic, 
	null cd_procedimento_solic, 
	null cd_carater_internacao, 
	null ie_clinica, 
	null nr_cartao_sus, 
	null ie_motivo_cobranca, 
	null nr_aih, 
	null nr_sequencia, 
	null ie_tipo_aih, 
	null nr_gestante_prenatal, 
	null dt_apresentacao, 
	' ' nr_cerih, 
	null cd_cid_principal, 
	null nr_cpf, 
	' ' nr_cmce, 
	' ' nr_cnrac, 
	' ' nr_cns_sol, 
	null cd_proced_solic, 
	null qt_procedimento, 
	' ' dt_competencia, 
	' ' nr_serie 
from  	sus_lote_autor	c, 
  	sus_laudo_paciente   	a, 
  	atendimento_paciente  	b 
where  a.nr_atendimento = b.nr_atendimento 
and   c.nr_sequencia = a.nr_seq_lote 

union
 
select	'Principal' ds_registro, 
	2 tp_registro, 
	e.nr_sequencia nr_lote, 
	coalesce(c.nr_ordem_laudo,c.nr_laudo_sus) nr_seq_laudo, 
	'' nr_identificacao, 
	null cd_cgc, 
	null nr_laudos, 
	'0' ie_tipo_laudo, 
	substr(to_char(c.dt_emissao,'yyyy')||'24'||adiciona_zeros_esquerda(to_char(coalesce(c.nr_ordem_laudo,c.nr_laudo_sus)),6)||calcula_Digito('MODULO11',to_char(c.dt_emissao,'yyyy')||'24'||adiciona_zeros_esquerda(to_char(coalesce(c.nr_ordem_laudo,c.nr_laudo_sus)),6)),1,13) nr_laudo, 
	substr(upper(a.nm_pessoa_fisica),1,50) nm_paciente, 
	upper(a.ie_sexo) ie_sexo, 
	substr(obter_dados_pf(a.cd_pessoa_fisica,'DN'),1,10) dt_nascimento, 
	obter_prontuario_paciente(a.cd_pessoa_fisica) nr_prontuario, 
	upper(substr(obter_compl_pf(a.cd_pessoa_fisica,1,'EN'),1,47)) ds_endereco, 
	lpad(substr(obter_compl_pf(a.cd_pessoa_fisica,1,'NR'),1,7),7,0) ds_numero, 
	upper(substr(obter_compl_pf(a.cd_pessoa_fisica,1,'CO'),1,20)) ds_complemento, 
	upper(substr(obter_compl_pf(a.cd_pessoa_fisica,1,'B'),1,20)) ds_bairro, 
	substr(obter_compl_pf(a.cd_pessoa_fisica,1,'CDM')||Calcula_Digito('MODULO10',obter_compl_pf(a.cd_pessoa_fisica,1,'CDM')),1,7) cd_municipio, 
	upper(substr(obter_compl_pf(a.cd_pessoa_fisica,1,'UF'),1,2)) ds_uf, 
	substr(obter_compl_pf(a.cd_pessoa_fisica,1,'CEP'),1,8) nr_cep, 
	b.dt_entrada dt_internacao, 
	b.dt_alta dt_alta, 
	CASE WHEN b.cd_motivo_alta=1 THEN 'A' WHEN b.cd_motivo_alta=2 THEN 'A' WHEN b.cd_motivo_alta=3 THEN 'A' WHEN b.cd_motivo_alta=4 THEN 'A' WHEN b.cd_motivo_alta=5 THEN 'A' WHEN b.cd_motivo_alta=6 THEN 'A' WHEN b.cd_motivo_alta=9 THEN 'A' WHEN b.cd_motivo_alta=11 THEN 'A' WHEN b.cd_motivo_alta=12 THEN 'A' WHEN b.cd_motivo_alta=10 THEN 'S' WHEN b.cd_motivo_alta=13 THEN 'S' WHEN b.cd_motivo_alta=8 THEN 'T' WHEN b.cd_motivo_alta=7 THEN 'O' END  ie_tipo_alta, 
	substr(obter_cpf_pessoa_fisica(c.cd_medico_requisitante),1,11) nr_cpf_solic, 
	c.cd_procedimento_solic, 
	b.ie_carater_inter_sus cd_carater_internacao, 
	CASE WHEN obter_clinica_conta(c.nr_interno_conta,'C')=1 THEN '3' WHEN obter_clinica_conta(c.nr_interno_conta,'C')=2 THEN '1' WHEN obter_clinica_conta(c.nr_interno_conta,'C')=3 THEN '2' WHEN obter_clinica_conta(c.nr_interno_conta,'C')=4 THEN '7'  ELSE '9' END  ie_clinica, 
	substr(obter_dados_pf(a.cd_pessoa_fisica,'CNS'),1,17) nr_cartao_sus, 
	coalesce(s.cd_motivo_cobranca,'00') ie_motivo_cobranca, 
	lpad(c.nr_aih,13,0) nr_aih, 
	c.nr_seq_aih nr_sequencia, 
	CASE WHEN c.ie_tipo_laudo_sus=11 THEN '5'  ELSE '0' END  ie_tipo_aih, 
	substr(s.nr_gestante_prenatal,1,15) nr_gestante_prenatal, 
	LOCALTIMESTAMP dt_apresentacao, 
	lpad('',15,0) nr_cerih, 
	c.cd_cid_principal, 
	substr(obter_dados_pf(a.cd_pessoa_fisica,'CPF'),1,11) nr_cpf, 
	lpad('',8,0) nr_cmce, 
	lpad('',8,0) nr_cnrac, 
	substr(obter_dados_pf(s.cd_medico_solic,'CNS'),1,15) nr_cns_sol, 
	null cd_proced_solic, 
	null qt_procedimento, 
	' ' dt_competencia, 
	' ' nr_serie 
from	sus_aih_unif			s, 
	pessoa_fisica 			a, 
	atendimento_paciente 		b, 
	sus_lote_autor     		e, 
	sus_laudo_paciente 			c 
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
and 	b.nr_atendimento  	= c.nr_atendimento 
and	b.nr_atendimento	= s.nr_atendimento 
and 	e.nr_sequencia 	= c.nr_seq_lote 
and	c.ie_tipo_laudo_sus not in (1,2,15) 

union
 
select	'Suplementar' ds_registro, 
	3 tp_registro, 
	c.nr_sequencia nr_lote, 
	coalesce(a.nr_ordem_laudo,a.nr_laudo_sus) nr_seq_laudo, 
	'' nr_identificacao, 
	null cd_cgc, 
	null nr_laudos, 
	CASE WHEN a.ie_tipo_laudo_sus=1 THEN '3' WHEN a.ie_tipo_laudo_sus=2 THEN '4' END  ie_tipo_laudo, 
	substr(to_char(a.dt_emissao,'yyyy')||'24'||adiciona_zeros_esquerda(to_char(coalesce(a.nr_ordem_laudo,a.nr_laudo_sus)),6)||calcula_Digito('MODULO11',to_char(a.dt_emissao,'yyyy')||'24'||adiciona_zeros_esquerda(to_char(coalesce(a.nr_ordem_laudo,a.nr_laudo_sus)),6)),1,13) nr_laudo, 
	null nm_paciente, 
	null ie_sexo, 
	null dt_nascimento, 
	null nr_prontuario, 
	null ds_endereco, 
	null ds_numero, 
	null ds_complemento, 
	null ds_bairro, 
	null cd_municipio, 
	null ds_uf, 
	null nr_cep, 
	null dt_internacao, 
	null dt_alta, 
