CREATE OR REPLACE VIEW tiss_material_solicitado_v (ds_versao, ie_origem, cd_material, ds_material, qt_solicitada, qt_autorizada, nr_sequencia_autor, cd_edicao_amb, cd_material_convenio, cd_edicao_relat, cd_cgc_fabricante, nr_prescricao, vl_unitario, ds_fabricante, nr_seq_mat_autor) AS select	/*+ INDEX (D AUTCONV_PK) */ '2.01.01' ds_versao,
	'AC' ie_origem,
	a.cd_material,
	substr(coalesce(a.ds_mat_convenio, obter_desc_material(a.cd_material)),1,60) ds_material,
	a.qt_solicitada,
	a.qt_autorizada,
	a.nr_sequencia_autor,
	substr(TISS_OBTER_ORIGEM_PRECO_MAT(null, a.cd_material, c.cd_estabelecimento, b.cd_convenio, b.cd_categoria, null, d.dt_autorizacao,null,'X',null,null, a.cd_material_tuss),1,20) cd_edicao_amb,
	lpad(CASE WHEN a.cd_material_convenio=0 THEN a.cd_material WHEN a.cd_material_convenio IS NULL THEN a.cd_material  ELSE a.cd_material_convenio END ,10,'0') cd_material_convenio,
	substr(TISS_OBTER_ORIGEM_PRECO_MAT(null, a.cd_material, c.cd_estabelecimento, b.cd_convenio, b.cd_categoria, null,  d.dt_autorizacao,null,'R',null,null, a.cd_material_tuss),1,20) cd_edicao_relat,
	a.cd_cgc_fabricante,
	(null)::numeric  nr_prescricao,
