SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW tre_indicador_agenda_exames_v (cd_convenio, ds_convenio, ie_status_agenda, ds_status, qt_agendamento, qt_minutos, dt_agenda, ds_procedencia, cd_procedencia, nm_paciente) AS SELECT  a.cd_convenio,
        substr(obter_nome_convenio(a.cd_convenio),1,40) ds_convenio,
        a.IE_STATUS_AGENDA ie_status_Agenda,
        substr(obter_valor_dominio(83, a.ie_status_agenda),1,100) ds_status,
        Count(a.ie_status_agenda) qt_agendamento,
        sum(a.NR_MINUTO_DURACAO) qt_minutos,
        a.dt_agenda,
        substr(Obter_Desc_Procedencia(a.cd_procedencia),1,100) ds_procedencia,
        a.cd_procedencia cd_procedencia,
        a.nm_paciente
FROM    agenda_paciente a
WHERE   a.ie_status_agenda <> 'L'
GROUP BY  a.cd_convenio,
          a.ie_status_agenda,
          a.dt_agenda,
          a.cd_procedencia,
          a.nm_paciente;

