CREATE OR REPLACE VIEW tre_view_com_union_v (nr_conta, dt_item, ds_item, qt_item, vl_item) AS SELECT  nr_interno_conta nr_conta,
        DT_CONTA dt_item,
        substr(obter_descricao_procedimento(cd_procedimento, ie_origem_proced),1,254) ds_item,
        QT_PROCEDIMENTO qt_item,
        VL_PROCEDIMENTO vl_item
FROM    PROCEDIMENTO_PACIENTE

UNION ALL

SELECT  nr_interno_conta nr_conta,
        DT_CONTA dt_item,
        substr(obter_desc_material(CD_MATERIAL),1,100) ds_item,
        QT_MATERIAL qt_item,
        VL_MATERIAL vl_item
FROM    MATERIAL_ATEND_PACIENTE;

