SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW trigger_v (nm_trigger, ds_tipo, nm_tabela, ie_status) AS select  substr(nm_objeto,1,30) nm_trigger,
         ( select	max(trigger_type) FROM	user_triggers b where b.trigger_name = w_inconsistencia_base.nm_objeto ) ds_tipo,
         substr(nm_tabela,1,30) nm_tabela,
         'DISABLED' ie_status
    from w_inconsistencia_base
    where  ds_motivo = obter_desc_expressao(308368);

