CREATE OR REPLACE VIEW unidade_livre_reserva_v (cd_setor_atendimento, cd_unidade_basica, cd_unidade_compl, nm_pessoa_fisica, cd_pessoa_fisica, ie_sexo, ie_status_unidade, ie_temporario, nr_seq_motivo_reserva) AS select	a.cd_setor_atendimento,
	a.cd_unidade_basica,
	a.cd_unidade_compl,
	b.nm_pessoa_fisica,
	b.cd_pessoa_fisica,
	b.ie_sexo,
	a.ie_status_unidade,
	a.ie_temporario,
	a.nr_seq_motivo_reserva
FROM	unidade_atendimento a,
	pessoa_fisica b
where	a.ie_status_unidade = 'R'
  and	a.cd_paciente_reserva = b.cd_pessoa_fisica
  and	a.ie_situacao = 'A'

union

select	a.cd_setor_atendimento,
	a.cd_unidade_basica,
	a.cd_unidade_compl,
	' ',
	cd_paciente_reserva,
	' ',
	a.ie_status_unidade,
	a.ie_temporario,
	(null)::numeric  nr_seq_motivo_reserva
from	unidade_atendimento a
where	a.ie_status_unidade in ('L', 'H', 'A', 'G')
  and	a.ie_situacao = 'A'
  and	not exists (select	b.nr_atendimento
      	from	Unidade_atendimento b
      	where	b.ie_status_unidade	= 'P'
        and	a.cd_setor_atendimento	= b.cd_setor_atendimento
        and	a.cd_unidade_basica	= b.cd_unidade_basica
	and	a.cd_unidade_compl	= b.cd_unidade_compl)

union

select	a.cd_setor_atendimento,
	a.cd_unidade_basica,
	a.cd_unidade_compl,
	' ',
	cd_paciente_reserva,
	'F',
	a.ie_status_unidade,
	a.ie_temporario,
	(null)::numeric  nr_seq_motivo_reserva
from	unidade_atendimento a
where	a.ie_status_unidade in ('L', 'H', 'G')
  and	a.ie_situacao = 'A'
  and (select	max(coalesce(d.ie_sexo,'A'))
      	from	Unidade_atendimento b,
		atendimento_paciente c,
		pessoa_fisica d
	where	b.ie_status_unidade	= 'P'
        and	a.cd_setor_atendimento	= b.cd_setor_atendimento
        and	a.cd_unidade_basica	= b.cd_unidade_basica
	and	a.cd_unidade_compl	= b.cd_unidade_compl
        and	b.nr_atendimento	= c.nr_atendimento
        and	c.cd_pessoa_fisica	= d.cd_pessoa_fisica) = 'F'

union

select	a.cd_setor_atendimento,
	a.cd_unidade_basica,
	a.cd_unidade_compl,
	' ',
	cd_paciente_reserva,
	'M',
	a.ie_status_unidade,
	a.ie_temporario,
	(null)::numeric  nr_seq_motivo_reserva
from	unidade_atendimento a
where	a.ie_status_unidade in ('L', 'H', 'G')
  and	a.ie_situacao = 'A'
  and (select min(coalesce(d.ie_sexo,'Z'))
      	from	Unidade_atendimento b,
		atendimento_paciente c,
		pessoa_fisica d
	where	b.ie_status_unidade	= 'P'
	and	a.cd_setor_atendimento	= b.cd_setor_atendimento
	and	a.cd_unidade_basica	= b.cd_unidade_basica
	and	a.cd_unidade_compl	= b.cd_unidade_compl
	and	b.nr_atendimento	= c.nr_atendimento
	and	c.cd_pessoa_fisica	= d.cd_pessoa_fisica) = 'M'

union

select	a.cd_setor_atendimento,
	a.cd_unidade_basica,
	a.cd_unidade_compl,
	' ',
	cd_paciente_reserva,
	' ',
	a.ie_status_unidade,
	a.ie_temporario,
	(null)::numeric  nr_seq_motivo_reserva
from	unidade_atendimento a
where	a.ie_status_unidade in ('L', 'H', 'G')
  and	a.ie_situacao = 'A'
  and (select	count(distinct d.ie_sexo)
      	from	Unidade_atendimento b,
		atendimento_paciente c,
		pessoa_fisica d
	where	b.ie_status_unidade	= 'P'
	and	a.cd_setor_atendimento	= b.cd_setor_atendimento
	and	a.cd_unidade_basica	= b.cd_unidade_basica
	and	a.cd_unidade_compl	= b.cd_unidade_compl
	and	b.nr_atendimento	= c.nr_atendimento
	and	c.cd_pessoa_fisica	= d.cd_pessoa_fisica) > 1;

