CREATE OR REPLACE VIEW urgencias_nom_v (ie_tipo_registro, cabecario, clues, folio, tipo_reg, curppaciente, nombre, primerapellido, segundoapellido, fechanacimiento, nacioextranjero, paisorigen, entidadnacimiento, edad, claveedad, sexo, afiliacion, gratuidad, numeroafiliacion, digitoverificador, resideextranjero, paisresidencia, entidadresidencia, municipioresidencia, localidadresidencia, codigopostal, seignoracp, tipovialidad, nombrevialidad, numeroexteriornumerico, tipoasentamiento, nombreasentamiento, telefono, atencionprehospitalaria, tiempotraslado, fechaingreso, horaingresso, tipourgencia, motivoatencion, tipocama, trasladotransitorio, cluestraslado, fechaalta, horaalta, mesestadistico, tiempoestancia, altapor, cluesreferido, mujerfertil, descripcionafeccionprincipal, codigocieafeccionprincipal, comorbilidad, afeccionreseleccionada, procedimiento, medicamento, planiras, planedas, numerosobres, ministeriopublico, foliocertificadodefuncion, curpresponsable, nombreresponsable, primerapellidoresponsable, segundoapellidoresponsable, cedulaprofesional, dt_entrada, cd_pessoa_fisica, cd_pessoa_fisica_medico, nr_atendimento) AS select 	0 ie_tipo_registro,
