	'fechaIngreso|horaIngresso|tipoUrgencia|motivoAtencion|tipoCama|trasladoTransitorio|cluesTraslado|fechaAlta'||
	'horaAlta|mesEstadistico|tiempoEstancia|altaPor|cluesReferido|mujerFertil|descripcionAfeccionPrincipal|codigoCIEAfeccionPrincipal|comorbilidad|afeccionPrincipalReseleccionada|procedimiento|' ||
	'medicamento|planIras|planEdas|numeroSobres|'||
