CREATE OR REPLACE VIEW usuarios_recebe_email_compra_v (nr_seq_regra, vl_dominio, ds_valor_dominio, ds_expressao) AS select	57 nr_seq_regra,
	a.vl_dominio,
	a.ds_valor_dominio,
	substr(OBTER_DESC_EXPRESSAO(a.CD_EXP_VALOR_DOMINIO),1,255) ds_expressao
FROM	valor_dominio a
where	cd_dominio = 4467
and	(a.vl_dominio)::numeric  in (1,2,3)

union

select	66 nr_seq_regra,
	a.vl_dominio,
	a.ds_valor_dominio,
	substr(OBTER_DESC_EXPRESSAO(a.CD_EXP_VALOR_DOMINIO),1,255) ds_expressao
from	valor_dominio a
where	cd_dominio = 4467
order by	ds_valor_dominio;

