CREATE OR REPLACE VIEW usuario_centro_custo_v (nm_usuario, nm_pessoa_fisica, cd_pessoa_fisica, cd_setor_atendimento, ds_setor_atendimento, cd_centro_custo, ds_centro_custo) AS select	a.nm_usuario,
	p.nm_pessoa_fisica,
	p.cd_pessoa_fisica,
	b.cd_setor_atendimento,
	b.ds_setor_atendimento,
	b.cd_centro_custo,
	c.ds_centro_custo
FROM pessoa_fisica p, usuario a, setor_atendimento b
