SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW view_mobile_announcement (nr_sequencia, ds_titulo, ds_informacoes, ds_link, dt_inicio, dt_fim) AS select
       a.NR_SEQUENCIA,
       a.DS_TITULO,
       a.DS_INFORMACOES,
       a.DS_LINK,
       a.DT_INICIO,
       a.DT_FIM
FROM pls_anuncio_mobile a
where ((LOCALTIMESTAMP BETWEEN a.DT_INICIO and a.DT_FIM) or (a.DT_FIM is null))
and a.ie_status = 'S'
order by a.DT_INICIO desc;

