CREATE OR REPLACE VIEW view_mobile_dependent (nm_segurado, dt_nascimento, nm_segurado_login, ds_email, nr_carteira, nr_seq_segurado, ds_plano, cd_pessoa_fisica, dt_adesao, dt_rescisao, dt_validade_carteira, nr_seq_titular, nr_seq_contrato, nr_seq_pagador, ie_pagador, ie_visualiza_titular, ie_visualiza_pagador, nr_seq_carteira, qt_max_caract_cart, ie_forma_val_cart_benef) AS select distinct pls_obter_dados_segurado(a.nr_sequencia, 'N') nm_segurado,
    c.dt_nascimento, 
    substr(pls_obter_dados_segurado(a.nr_sequencia, 'N'),1,10) nm_segurado_login, 
    pls_obter_dados_segurado(a.nr_sequencia, 'E') ds_email, 
    b.cd_usuario_plano nr_carteira,  
    a.nr_sequencia nr_seq_segurado,  
    pls_obter_dados_segurado(a.nr_sequencia, 'P') ds_plano, 
    a.cd_pessoa_fisica, 
    to_char(a.dt_contratacao,'dd/mm/yyyy') dt_adesao,			 
    to_char(a.dt_rescisao,'dd/mm/yyyy') dt_rescisao,			 
    to_char(b.dt_validade_carteira,'dd/mm/yyyy') dt_validade_carteira,			 
    a.nr_seq_titular nr_seq_titular,			 
    a.nr_seq_contrato nr_seq_contrato, 
    a.nr_seq_pagador nr_seq_pagador,  
    pls_obter_se_pagador(a.nr_sequencia) ie_pagador, 
    pls_obter_regra_contrato_web(null, a.nr_sequencia, 'TM') ie_visualiza_titular,  
    pls_obter_regra_contrato_web(null, a.nr_sequencia, 'PM') ie_visualiza_pagador,  
    pls_obter_dados_carteira(null, b.cd_usuario_plano, 'SC') nr_seq_carteira,  
    pls_obter_max_caract_cart_web qt_max_caract_cart, 		  
    pls_parametro_operadora_web('FVC') ie_forma_val_cart_benef 
FROM  pessoa_fisica c, 
   pls_segurado a,    
