CREATE OR REPLACE VIEW view_mobile_login_beneficiary (nm_segurado, dt_nascimento, cd_pessoa_fisica, nr_seq_plano, nm_segurado_login, ds_email, nr_carteira, ie_situacao, nr_seq_segurado, ds_plano, cd_estabelecimento, dt_adesao, dt_rescisao, dt_validade_carteira, nr_seq_titular, nr_seq_contrato, nr_seq_pagador, ie_pagador, ie_visualiza_titular, ie_visualiza_pagador, nr_seq_carteira, qt_max_caract_cart, ie_forma_val_cart_benef, nr_sequencia, ds_tec) AS select distinct pls_obter_dados_segurado(b.nr_sequencia, 'N') nm_segurado,
    d.dt_nascimento, 
    b.cd_pessoa_fisica, 
    b.nr_seq_plano, 
