    pls_obter_dados_segurado(b.nr_sequencia, 'E') ds_email, 
    c.cd_usuario_plano nr_carteira,  
    a.ie_situacao,          
    b.nr_sequencia nr_seq_segurado,  
    pls_obter_dados_segurado(b.nr_sequencia, 'P') ds_plano, 
    coalesce(a.cd_estabelecimento, 1) cd_estabelecimento, 			 
    to_char(b.dt_contratacao,'dd/mm/yyyy') dt_adesao,			 
    to_char(b.dt_rescisao,'dd/mm/yyyy') dt_rescisao,			 
    to_char(c.dt_validade_carteira,'dd/mm/yyyy') dt_validade_carteira,			 
    b.nr_seq_titular nr_seq_titular,			 
    b.nr_seq_contrato nr_seq_contrato, 
    b.nr_seq_pagador nr_seq_pagador,  
    pls_obter_se_pagador(b.nr_sequencia) ie_pagador, 
    pls_obter_regra_contrato_web(null, b.nr_sequencia, 'TM') ie_visualiza_titular,  
    pls_obter_regra_contrato_web(null, b.nr_sequencia, 'PM') ie_visualiza_pagador,  
    pls_obter_dados_carteira(null, c.cd_usuario_plano, 'SC') nr_seq_carteira,  
    pls_obter_max_caract_cart_web qt_max_caract_cart, 		  
    pls_parametro_operadora_web('FVC') ie_forma_val_cart_benef,  
    a.nr_sequencia, 		 
    a.ds_tec 
FROM  pessoa_fisica d, 
    pls_segurado_web a,      
    pls_segurado_carteira c,   
    pls_segurado b        
where a.nr_seq_segurado  = b.nr_sequencia   
and  b.nr_sequencia   = c.nr_seq_segurado  
and  b.cd_pessoa_fisica = d.cd_pessoa_fisica 
and  a.ie_situacao    = 'A' 
and  pls_obter_dados_segurado(b.nr_sequencia, 'CS') = 'A';

