CREATE OR REPLACE VIEW wkp_card_accept_errorwarning_v (nr_case, nr_record_id, ie_record_type, dt_admission, cd_error_warning, scoring_comments, efg, error_warning_reason) AS SELECT
	a.NR_CASE,
	a.NR_RECORD_ID,
	a.IE_RECORD_TYPE,
	a.DT_ADMISSION,
	a.CD_ERROR_WARNING,
	SUBSTR(b.DS_JUSTIFICATIVA,1,200) SCORING_COMMENTS,
	'EFG (According to Massimo, only the external approval flag is necessary)' as EFG,
	VL_ERROR_WARNING ERROR_WARNING_REASON
FROM	SCORING_ERROR_WARNING a,
	SCORING_ERROR_WARNING_JUST b
	where a.NR_SEQUENCIA = b.NR_SEQ_SCORING_ERROR_WARNING;

