SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW wl_horarios_atrasados_v (dt_horario, nr_seq_mat_cpoe) AS select 	c.dt_horario,
	a.nr_sequencia nr_seq_mat_cpoe
FROM	cpoe_material a,
	prescr_material b,
	prescr_mat_hor c
where  a.nr_sequencia = b.nr_seq_mat_cpoe
and a.cd_material = b.cd_material
and b.nr_prescricao = c.nr_prescricao
and	b.nr_sequencia = c.nr_seq_material
and	c.dt_fim_horario is null
and	c.dt_interrupcao is null    
and	c.dt_lib_horario is not null   
and	c.dt_horario < (LOCALTIMESTAMP - ( (coalesce(obter_param_usuario_logado(1113, 174), 0))::numeric  / 1440))
and	c.dt_recusa is null    
and	c.dt_suspensao is null  
and	((c.ie_agrupador = 1) or (c.ie_agrupador = 4 and coalesce(obter_status_solucao_prescr(1, c.nr_prescricao, c.nr_seq_solucao),'N') in ('N','P')))
and	coalesce(c.ie_horario_especial,'N') = 'N';

