CREATE OR REPLACE VIEW w_authorization_v (nr_sequencia, cd_procedimento, cd_procedimento_tuss, qt_solicitada, vl_unitario) AS SELECT
	autconv.NR_SEQUENCIA, 
	procaut.CD_PROCEDIMENTO, 
	procaut.CD_PROCEDIMENTO_TUSS, 
	procaut.QT_SOLICITADA, 
	OBTER_DADOS_CONTA_ITEM_AUTOR(autconv.NR_SEQUENCIA,autconv.NR_PRESCRICAO,procaut.NR_SEQ_PRESCRICAO,'V',1) VL_UNITARIO 
FROM 
	PROCEDIMENTO_AUTORIZADO procaut 
LEFT JOIN AUTORIZACAO_CONVENIO autconv on autconv.NR_SEQUENCIA = procaut.NR_SEQUENCIA_AUTOR;

