CREATE OR REPLACE VIEW w_eis_adesao_prev_dai_v (cd_setor, ds_setor_atendimento, dt_avaliacao, ds_sexo, ie_faixa_etaria, ie_prescrito, na) AS select	distinct
	Obter_Setor_Atendimento(b.nr_atendimento) cd_setor, 
	SUBSTR(obter_nome_setor(Obter_Setor_Atendimento(b.nr_atendimento)),1,255) ds_Setor_Atendimento,	 
	TRUNC(b.dt_avaliacao) dt_avaliacao, 
	SUBSTR(obter_sexo_pf(OBTER_PESSOA_ATENDIMENTO(b.nr_atendimento,'C'),'D'),1,20) ds_sexo, 
	SUBSTR(Obter_desc_ficha_DAI(null,'E',b.dt_avaliacao,obter_data_nascto_pf(OBTER_PESSOA_ATENDIMENTO(b.nr_atendimento,'C'))),1,80) ie_faixa_etaria, 
	SUBSTR(Obter_desc_ficha_DAI(b.nr_atendimento,'PO',b.dt_avaliacao),1,10) ie_prescrito,	 
	b.nr_atendimento NA 
FROM	escala_eif b,	 
	eif_escala d 
where	d.nr_sequencia = b.nr_seq_escala 
and	b.dt_inativacao is null 
and	UPPER(d.ds_escala) like UPPER('%dermatite%') 
and	obter_resultado_escala_eif(b.nr_sequencia,'R') = 'N';

