CREATE OR REPLACE VIEW w_eis_distr_pps_v (dt_avaliacao, setor, ds_setor_atendimento, ie_sexo, ds_sexo, ie_faixa_etaria, cd_estabelecimento, ds_estabelecimento, ds_pontuacao, tipo, ds_atendimento) AS select distinct
	a.dt_avaliacao, 
	b.cd_setor_atendimento setor, 
	obter_nome_setor(b.cd_setor_atendimento) ds_setor_atendimento, 
	c.ie_sexo ie_sexo, 
	obter_valor_dominio(4,c.ie_sexo) ds_sexo,	 
