CREATE OR REPLACE VIEW w_eis_escala_prm_distr_cons_v (cd_setor_atendimento, ds_setor_atendimento, dt_evento, cd_empresa, cd_estabelecimento, ie_consequencia, ds_unidade, ds_nivel_conseq, nr_seq_area, ds_area, qt_total, nr_atendimento, ds_alto_risco) AS select	distinct
	e.cd_setor_atendimento, 
	substr(obter_nome_setor(e.cd_setor_atendimento),1,255) ds_Setor_Atendimento,	 
	b.dt_evento, 
