	(obter_dados_escala_prm(null,'T',b.dt_evento,null))::numeric  qt_total, 
	b.nr_atendimento, 
	CASE WHEN(obter_se_material_risco_PRM(e.nr_sequencia))='S' THEN 'Sim'  ELSE 'Não' END  ds_alto_risco	 
FROM estabelecimento f, qua_evento_paciente b, atendimento_paciente a, prm_ficha_ocorrencia e
