CREATE OR REPLACE VIEW w_eis_queda_taxa_turno_v (cd_setor_queda, dt_queda, ds_setor_queda, dt_evento, cd_empresa, cd_estabelecimento, ds_unidade, ds_turno, nr_atendimento) AS select	distinct
	e.cd_setor_queda, 
	e.dt_queda, 
	substr(obter_nome_setor(e.cd_setor_queda),1,255) ds_Setor_Queda,	 
