SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION adds_pck.calcular_score_total_data ( nr_atendimento_p bigint, nr_seq_adds_p bigint, dt_sinal_vital_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_score_w				varchar(5);
qt_score_w				bigint;

C01 CURSOR FOR
	SELECT	*
	from	table(adds_pck.obter_sinais_vitais(nr_atendimento_p,nr_seq_adds_p,dt_sinal_vital_p,dt_sinal_vital_p,'N'));
BEGIN

ie_score_w	:= null;
qt_score_w	:= 0;

for r_c01 in c01  loop
	begin
	if (r_c01.ie_score_adds	= 'E') then
		ie_score_w	:= 'E';
		exit;
	else

		qt_score_w	:= qt_score_w + coalesce((r_c01.ie_score_adds)::numeric ,0);
		ie_score_w	:= qt_score_w;
		if (qt_score_w	>= 8) then
			ie_score_w	:= 'E';
			exit;
		end if;
	end if;

	end;
end loop;

return ie_score_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
