WHERE z.nr_seq_exame = x.nr_seq_exame and y.cd_procedimento = x.cd_procedimento and y.ie_origem_proced = x.ie_origem_proced and w.nr_sequencia = x.nr_seq_proc_interno and x.nr_prescricao = a.nr_prescricao and obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S' and a.nr_atendimento = nr_atendimento_p and coalesce(a.dt_validade_prescr,a.dt_inicio_prescr + 24/24) between dt_validade_limite_p and dt_fim_w and (obter_data_lib_proc_adep(a.dt_liberacao, a.dt_liberacao_medico, ie_data_lib_proced_p) IS NOT NULL AND (obter_data_lib_proc_adep(a.dt_liberacao, a.dt_liberacao_medico, ie_data_lib_proced_p))::text <> '') and coalesce(w.ie_tipo,'O') not in ('G','BS') and coalesce(w.ie_ivc,'N') <> 'S' and coalesce(w.ie_ctrl_glic,'XPTO') <> 'CIG' and (x.nr_seq_proc_interno IS NOT NULL AND x.nr_seq_proc_interno::text <> '') and coalesce(x.nr_seq_prot_glic::text, '') = '' and (x.nr_seq_exame IS NOT NULL AND x.nr_seq_exame::text <> '') and coalesce(x.nr_seq_origem::text, '') = '' and coalesce(x.nr_seq_solic_sangue::text, '') = '' and coalesce(x.nr_seq_derivado::text, '') = '' and coalesce(x.nr_seq_exame_sangue::text, '') = '' and coalesce(x.dt_suspensao::text, '') = '' and coalesce(a.dt_suspensao::text, '') = '' and ((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w		= 'S')) and ((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = '')) and ((ie_so_proc_setor_usuario_p = 'N') or (adep_obter_se_setor_proc_user(nm_usuario_p, x.cd_setor_atendimento) = 'S')) and obter_se_prescr_vig_adep(a.dt_prescricao,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S' and ie_regra_inclusao_p <> 'N' and ((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p)) and not exists (
				select	1
				from	prescr_proc_hor c
				where	c.cd_procedimento = y.cd_procedimento
				and	c.ie_origem_proced = y.ie_origem_proced
				and	c.nr_prescricao = x.nr_prescricao
				and	c.nr_seq_procedimento = x.nr_sequencia
				and	c.nr_prescricao = a.nr_prescricao
				and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S') and ((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440)) group by
			a.nr_prescricao,
			x.nr_sequencia,
			x.cd_procedimento,
			x.ie_acm,
			x.ie_se_necessario,		
			z.nm_exame,
			x.cd_intervalo,
			x.qt_procedimento,
			v.ds_material_exame,
			x.ie_suspenso,
			x.nr_seq_proc_interno,
			a.dt_liberacao_medico,
			a.dt_liberacao,
			clock_timestamp(),
			x.ds_horarios,
			x.nr_seq_proc_cpoe,
			CASE WHEN ie_regra_inclusao_p='R' THEN  adep_obter_inclusao_regra('PROC', a.nr_prescricao, cd_estabelecimento_p, cd_setor_usuario_p, cd_perfil_p, null, x.cd_procedimento, x.ie_origem_proced, x.nr_seq_proc_interno, a.cd_setor_Atendimento, x.cd_setor_Atendimento, x.nr_seq_exame)  ELSE 'S' END 
		) alias109
	group by
		CASE WHEN coalesce(nr_seq_proc_cpoe::text, '') = '' THEN  nr_prescricao  ELSE null END ,
		CASE WHEN coalesce(nr_seq_proc_cpoe::text, '') = '' THEN  nr_seq_procedimento  ELSE null END ,
		cd_procedimento,
		ds_procedimento,
		ie_acm_sn,	
		cd_intervalo,
		qt_procedimento,
		ds_prescricao,
		ie_suspenso,
		nr_seq_proc_interno,
		ie_lib_pend_rep,
		ie_horario,
		nr_seq_proc_cpoe,
		ie_apresenta_item;			
	
BEGIN
	open c01;
	loop
	fetch c01 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
			nr_seq_procedimento_w,
			cd_procedimento_w,
			ds_procedimento_w,
			ie_acm_sn_w,		
			cd_intervalo_w,
			qt_procedimento_w,
			ds_prescricao_w,
			ie_status_w,
			nr_seq_proc_interno_w,
			ds_observacao_w,
			nr_seq_proc_cpoe_w,			
			ds_diluicao_w,
			ie_apresenta_item_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		--Coleta
		if (ie_regra_inclusao_p = 'S') or (ie_regra_inclusao_p = 'R' and ie_apresenta_item_w = 'S') then
		dt_suspensao_cpoe_w := cpoe_obter_dt_suspensao(nr_seq_proc_cpoe_w, 'P');
		if	dt_suspensao_cpoe_w <= clock_timestamp() then
			ds_procedimento_w := wheb_mensagem_pck.get_texto(820376) || ' ' || substr(ds_procedimento_w,1,240);
		elsif (dt_suspensao_cpoe_w > clock_timestamp()) then
			ds_procedimento_w := '(' || wheb_mensagem_pck.get_texto(1061446, 'DT_SUSPENSAO=' || TO_CHAR(dt_suspensao_cpoe_w, pkg_date_formaters.localize_mask('short', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))) || ') ' || substr(ds_procedimento_w,1,240);
		end if;
		select	nextval('w_adep_t_seq')
		into STRICT	nr_seq_wadep_w
		;
		insert into w_adep_t(
			nr_sequencia,
			nm_usuario,
			ie_tipo_item,
			nr_prescricao,
			nr_seq_item,		
			cd_item,
			ds_item,
			ie_acm_sn,		
			cd_intervalo,
			qt_item,
			ds_prescricao,
			ie_status_item,
			nr_seq_proc_interno,
			nr_agrupamento,
			ie_diferenciado,
			ds_observacao,
			nr_seq_cpoe,
			ds_diluicao)
		values (
			nr_seq_wadep_w,
			nm_usuario_p,
			CASE WHEN ie_aba_p='L' THEN 'P'  ELSE 'L' END ,
			current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
			nr_seq_procedimento_w,
			cd_procedimento_w,
			substr(ds_procedimento_w,1,240),
			ie_acm_sn_w,
			cd_intervalo_w,
			qt_procedimento_w,
			ds_prescricao_w,
			ie_status_w,
			nr_seq_proc_interno_w,
			0,
			'N',
			ds_observacao_w,
			nr_seq_proc_cpoe_w,
			ds_diluicao_w);
		commit;
		end if;
		end;
	end loop;
	close c01;
	if (ie_lib_pend_rep_p = 'S') then
		begin
		open c02;
		loop
		fetch c02 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_procedimento_w,
				cd_procedimento_w,
				ds_procedimento_w,
				ie_acm_sn_w,		
				cd_intervalo_w,
				qt_procedimento_w,
				ds_prescricao_w,
				ie_status_w,
				nr_seq_proc_interno_w,
				current_setting('adep_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_horario_w,
				nr_seq_proc_cpoe_w,
				ie_apresenta_item_w;				
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			--Coleta
			if (ie_regra_inclusao_p = 'S') or (ie_regra_inclusao_p = 'R' and ie_apresenta_item_w = 'S') then
			dt_suspensao_cpoe_w := cpoe_obter_dt_suspensao(nr_seq_proc_cpoe_w, 'P');
			if	dt_suspensao_cpoe_w <= clock_timestamp() then
				ds_procedimento_w := wheb_mensagem_pck.get_texto(820376) || ' ' || substr(ds_procedimento_w,1,240);
			elsif (dt_suspensao_cpoe_w > clock_timestamp()) then
				ds_procedimento_w := '(' || wheb_mensagem_pck.get_texto(1061446, 'DT_SUSPENSAO=' || TO_CHAR(dt_suspensao_cpoe_w, pkg_date_formaters.localize_mask('short', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))) || ') ' || substr(ds_procedimento_w,1,240);
			end if;
			select	nextval('w_adep_t_seq')
			into STRICT	nr_seq_wadep_w
			;
			insert into w_adep_t(
				nr_sequencia,
				nm_usuario,
				ie_tipo_item,
				nr_prescricao,
				nr_seq_item,		
				cd_item,
				ds_item,
				ie_acm_sn,		
				cd_intervalo,
				qt_item,
				ds_prescricao,
				ie_status_item,
				nr_seq_proc_interno,
				nr_agrupamento,
				ie_diferenciado,
				ie_pendente_liberacao,
				ie_horario,
				nr_seq_cpoe
				)
			values (
				nr_seq_wadep_w,
				nm_usuario_p,
				CASE WHEN ie_aba_p='L' THEN 'P'  ELSE 'L' END ,				
				current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_procedimento_w,
				cd_procedimento_w,
				substr(ds_procedimento_w,1,240),
				ie_acm_sn_w,
				cd_intervalo_w,
				qt_procedimento_w,
				ds_prescricao_w,
				ie_status_w,
				nr_seq_proc_interno_w,
				0,
				'N',
				current_setting('adep_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_horario_w,
				nr_seq_proc_cpoe_w
				);
			commit;
			end if;
			end;
		end loop;
		close c02;	
		end;
	end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_gerar_horarios_pck.adep_obter_coleta ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, ie_aba_p text, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_lib_pend_rep_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_data_lib_proced_p text, ie_so_proc_setor_usuario_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) FROM PUBLIC;
