CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_obter_dieta_oral_servico ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_lib_pend_rep_p text, ie_exibir_suspensos_p text, ie_horarios_dietas_orais_p text, ie_exibe_sem_lib_farm_p text, ie_exibe_dietas_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE

	cd_refeicao_w		varchar(15);
	ds_refeicao_w		varchar(255);
	ie_lib_pend_rep_w	varchar(1);
	nr_seq_wadep_w		bigint;
	ie_status_item_w	varchar(1);
	ie_horario_w		varchar(1);
	current_setting('adep_gerar_horarios_pck.iedescdietasagrupadas_w')::varchar(1)	varchar(1);
	ie_recem_nato_w		varchar(1) := adep_gerar_horarios_pck.get_ie_recem_nato();	
	nr_seq_dieta_cpoe_w	prescr_dieta.nr_seq_dieta_cpoe%type;
	dt_suspensao_cpoe_w	timestamp;
	C01 CURSOR FOR
	SELECT	CASE WHEN GetIeAgruparServicos='S' THEN  to_char(d.nr_sequencia)  ELSE to_char(x.nr_sequencia) END ,
			CASE WHEN current_setting('adep_gerar_horarios_pck.iedescdietasagrupadas_w')::varchar(1)='S' THEN  substr(OBTER_NOME_DIETA(d.cd_dieta),1,240)  ELSE b.ds_servico END ,
			d.nr_seq_dieta_cpoe
	from	prescr_dieta d,
			nut_servico b,
			prescr_medica a,
			nut_atend_serv_dia x
	where	d.nr_prescricao	= a.nr_prescricao
	and 	x.nr_atendimento = a.nr_atendimento
	and	x.nr_seq_servico = b.nr_sequencia
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	Obter_se_servico_vig_dieta(a.dt_inicio_prescr, a.dt_validade_prescr, a.nr_Atendimento, '1', ie_exibir_hor_realizados_p, ie_exibir_hor_suspensos_p) = 'S'
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	x.dt_servico between dt_inicial_horarios_p and dt_final_horarios_p
	and	(((coalesce(a.dt_liberacao, a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao, a.dt_liberacao_medico))::text <> '')) or
		 ((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(d.dt_suspensao::text, '') = ''))
	and 	((coalesce(b.IE_TIPO_PRESCRICAO_SERVICO::text, '') = '') or (b.IE_TIPO_PRESCRICAO_SERVICO = '1'))
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(x.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and 	x.ie_status_adep = 'Y'
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))	
	and	((ie_exibir_suspensos_p = 'S' and
			((obter_se_rep_cpoe(a.nr_prescricao) = 'S' and (cpoe_obter_dt_suspensao(d.nr_seq_dieta_cpoe, 'N') IS NOT NULL AND (cpoe_obter_dt_suspensao(d.nr_seq_dieta_cpoe, 'N'))::text <> '')) or (obter_se_rep_cpoe(a.nr_prescricao) = 'N' and (d.dt_suspensao IS NOT NULL AND d.dt_suspensao::text <> ''))) or (substr(obter_se_prescricao_nut_dia(d.nr_prescricao, x.nr_sequencia),1,2) = 'S')))
	and	getIe_apresentar_dieta_nao_lib = 'S'
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	group by CASE WHEN GetIeAgruparServicos='S' THEN  to_char(d.nr_sequencia)  ELSE to_char(x.nr_sequencia) END ,
		 CASE WHEN current_setting('adep_gerar_horarios_pck.iedescdietasagrupadas_w')::varchar(1)='S' THEN  substr(OBTER_NOME_DIETA(d.cd_dieta),1,240)  ELSE b.ds_servico END ,
		 d.nr_seq_dieta_cpoe
	
union

	SELECT	CASE WHEN GetIeAgruparServicos='S' THEN  to_char(d.nr_sequencia)  ELSE to_char(x.nr_sequencia) END ,
			CASE WHEN current_setting('adep_gerar_horarios_pck.iedescdietasagrupadas_w')::varchar(1)='S' THEN  substr(OBTER_NOME_DIETA(d.cd_dieta),1,240)  ELSE b.ds_servico END ,
			d.nr_seq_dieta_cpoe
	from	prescr_dieta d,
		nut_servico b,
		nut_atend_serv_dia_rep c,
		prescr_medica a,
		nut_atend_serv_dia x	
	where	d.nr_prescricao	= a.nr_prescricao
	and 	x.nr_atendimento = a.nr_atendimento
	and	x.nr_seq_servico = b.nr_sequencia
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	a.nr_prescricao = c.nr_prescr_oral
	and	c.nr_seq_serv_dia = x.nr_sequencia
	and	x.dt_servico between dt_inicial_horarios_p and dt_final_horarios_p
	and	Obter_se_servico_vig_dieta(a.dt_inicio_prescr, a.dt_validade_prescr, a.nr_Atendimento, '1', ie_exibir_hor_realizados_p, ie_exibir_hor_suspensos_p) = 'S'
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	(((coalesce(a.dt_liberacao, a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao, a.dt_liberacao_medico))::text <> '')) or
		 ((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.ie_prescr_nutricao, 'N') = 'S')))
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))		  
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(d.dt_suspensao::text, '') = ''))
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(x.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and 	((coalesce(b.IE_TIPO_PRESCRICAO_SERVICO::text, '') = '') or (b.IE_TIPO_PRESCRICAO_SERVICO = '1'))
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and 	x.ie_status_adep <> 'Y'
	and	substr(obter_se_prescricao_nut_dia(a.nr_prescricao, x.nr_sequencia),1,2) = 'S'
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	group by CASE WHEN GetIeAgruparServicos='S' THEN  to_char(d.nr_sequencia)  ELSE to_char(x.nr_sequencia) END ,
		CASE WHEN current_setting('adep_gerar_horarios_pck.iedescdietasagrupadas_w')::varchar(1)='S' THEN  substr(OBTER_NOME_DIETA(d.cd_dieta),1,240)  ELSE b.ds_servico END ,
		d.nr_seq_dieta_cpoe
	order by 2;
	
BEGIN
	PERFORM set_config('adep_gerar_horarios_pck.iedescdietasagrupadas_w', getIeDescDietasAgrupadas, false);
	open c01;
	loop
	fetch c01 into	cd_refeicao_w,
					ds_refeicao_w,
					nr_seq_dieta_cpoe_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		--Dieta
		dt_suspensao_cpoe_w := cpoe_obter_dt_suspensao(nr_seq_dieta_cpoe_w, 'N');
		if	dt_suspensao_cpoe_w <= clock_timestamp() then
			ds_refeicao_w := wheb_mensagem_pck.get_texto(820376) || ' ' || substr(ds_refeicao_w,1,240);
		elsif (dt_suspensao_cpoe_w > clock_timestamp()) then
			ds_refeicao_w := '(' || wheb_mensagem_pck.get_texto(1061446, 'DT_SUSPENSAO=' || TO_CHAR(dt_suspensao_cpoe_w, pkg_date_formaters.localize_mask('short', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))) || ') ' || substr(ds_refeicao_w,1,240);
		end if;
		select	nextval('w_adep_t_seq')
		into STRICT	nr_seq_wadep_w
		;
		insert into w_adep_t(
			nr_sequencia,
			nm_usuario,
			ie_tipo_item,
			cd_item,
			ds_item,
			ie_acm_sn,
			ie_diferenciado,
			nr_seq_proc_interno,
			nr_agrupamento,
			nr_seq_cpoe
			)
		values (
			nr_seq_wadep_w,
			nm_usuario_p,
			'DE',
			cd_refeicao_w,
			substr(ds_refeicao_w,1,240),
			'N',
			'N',
			0,
			0,
			nr_seq_dieta_cpoe_w
			);
		commit;
		end;
	end loop;
	close c01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
