																						cd_setor_usuario_p,
																						cd_perfil_p,
																						null,
																						null,
																						null,
																						null,
																						a.cd_setor_Atendimento,
																						null,
																						null,
																						null) = 'S'))) and ((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p)) and ((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w		= 'S')) and (((getItensGasTerm = 'S') and (substr(Obter_Status_Gasoterapia(x.nr_sequencia, 'C'),1,80) in ('I','IN','R'))) or
						(((obter_se_acm_sn_agora_especial(CASE WHEN x.ie_inicio='ACM' THEN  'S'  ELSE 'N' END , CASE WHEN x.ie_inicio='D' THEN  'S'  ELSE 'N' END , CASE WHEN x.ie_inicio='A' THEN  'S'  ELSE 'N' END , 'N') = 'N') and
						obter_se_gas_hor_lib(x.nr_prescricao, x.nr_sequencia, dt_inicial_horarios_p, dt_final_horarios_p) = 'S')	or
						((obter_se_acm_sn_agora_especial(CASE WHEN x.ie_inicio='ACM' THEN  'S'  ELSE 'N' END , CASE WHEN x.ie_inicio='D' THEN  'S'  ELSE 'N' END , CASE WHEN x.ie_inicio='A' THEN  'S'  ELSE 'N' END , 'N') = 'S') and (obter_se_prescr_vig_adep(a.dt_prescricao, a.dt_validade_prescr, dt_inicial_horarios_p, dt_final_horarios_p) = 'S')))) ) alias60
		where 	((ie_exibir_hor_realizados_p = 'S') or ((ie_exibir_hor_realizados_p = 'N') and coalesce(adep_obter_status_gas_cpoe(nr_seq_gas_cpoe, ie_exibir_suspensos_p),'N') <> 'T'))
		and		((ie_exibir_suspensos_p = 'S') or ((ie_exibir_suspensos_p = 'N') and
            CASE WHEN coalesce(nr_seq_gas_cpoe::text, '') = '' THEN  coalesce(substr(Obter_Status_Gasoterapia(nr_sequencia, 'C'),1,10),'N')  ELSE coalesce(adep_obter_status_gas_cpoe(nr_seq_gas_cpoe, ie_exibir_suspensos_p),'N') END  <> 'S'))
		group by
			nr_seq_gas,
			ds_gas,
			ie_acm_sn,
			cd_intervalo,
			qt_gasoterapia,
			ds_config_gas,
			ie_lib_pend_rep,
			nr_seq_gas_cpoe,
			ie_item_ordem;

	
BEGIN
    	
	if (ie_gas_horario_p = 'N' and (((ie_html_p = 'N' and ie_java_p = 'N') or (ie_java_p = 'S' and verificar_cpoe_ativa(cd_estabelecimento_p, nr_atendimento_p) = 'N')) or getIeGasSeparada = 'S')) then

        open c01;
		loop
		fetch c01 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_gasoterapia_w,
				nr_seq_gas_w,
				ds_gas_w,
				ie_acm_sn_w,		
				cd_intervalo_w,
				qt_gasoterapia_w,
				ds_prescricao_w,
				ds_config_gas_w,
				current_setting('adep_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_status_gas_w,
				nr_seq_gas_cpoe_w,
				ie_item_ordem_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			--Gasoterapia
			dt_suspensao_cpoe_w := cpoe_obter_dt_suspensao(nr_seq_gas_cpoe_w, 'G');
			if	dt_suspensao_cpoe_w <= clock_timestamp() then
				ds_gas_w := wheb_mensagem_pck.get_texto(820376) || ' ' || substr(ds_gas_w,1,240);
			elsif (dt_suspensao_cpoe_w > clock_timestamp()) then
				ds_gas_w := '(' || wheb_mensagem_pck.get_texto(1061446, 'DT_SUSPENSAO=' || TO_CHAR(dt_suspensao_cpoe_w, pkg_date_formaters.localize_mask('short', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))) || ') ' || substr(ds_gas_w,1,240);
			end if;
			select	nextval('w_adep_t_seq')
			into STRICT	nr_seq_wadep_w
			;
			insert into w_adep_t(
				nr_sequencia,
				nm_usuario,
				ie_tipo_item,
				nr_prescricao,
				nr_seq_item,		
				cd_item,
				ds_item,
				ie_acm_sn,		
				cd_intervalo,
				qt_item,
				ds_prescricao,
				ds_diluicao,
				nr_agrupamento,
				ie_diferenciado,
				ie_pendente_liberacao,
				ie_status_item,
				nr_seq_cpoe,
				ie_item_ordem,
				nr_prescricoes)
			values (
				nr_seq_wadep_w,
				nm_usuario_p,
				'O',
				current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_gasoterapia_w,
				nr_seq_gas_w,
				substr(ds_gas_w,1,240),
				ie_acm_sn_w,
				cd_intervalo_w,
				qt_gasoterapia_w,
				ds_prescricao_w,
				ds_config_gas_w,
				0,
				'N',
				current_setting('adep_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_status_gas_w,
				nr_seq_gas_cpoe_w,
				ie_item_ordem_w,
				current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type);
			commit;
			end;
		end loop;
		close c01;
		
	elsif	(ie_gas_horario_p = 'S' and ((ie_html_p = 'N' and ie_java_p = 'N') or getIeGasSeparada = 'S')) then
    	
		open c02;
		loop
		fetch c02 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_gasoterapia_w,
				nr_seq_gas_w,
				ds_gas_w,
				ie_acm_sn_w,		
				cd_intervalo_w,
				qt_gasoterapia_w,
				ds_prescricao_w,
				ds_config_gas_w,
				current_setting('adep_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_status_gas_w,
				nr_seq_gas_cpoe_w,
				ie_item_ordem_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			--Gasoterapia
			dt_suspensao_cpoe_w := cpoe_obter_dt_suspensao(nr_seq_gas_cpoe_w, 'G');
			if	dt_suspensao_cpoe_w <= clock_timestamp() then
				ds_gas_w := wheb_mensagem_pck.get_texto(820376) || ' ' || substr(ds_gas_w,1,240);
			elsif (dt_suspensao_cpoe_w > clock_timestamp()) then
				ds_gas_w := '(' || wheb_mensagem_pck.get_texto(1061446, 'DT_SUSPENSAO=' || TO_CHAR(dt_suspensao_cpoe_w, pkg_date_formaters.localize_mask('short', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))) || ') ' || substr(ds_gas_w,1,240);
			end if;
			select	nextval('w_adep_t_seq')
			into STRICT	nr_seq_wadep_w
			;
			insert into w_adep_t(
				nr_sequencia,
				nm_usuario,
				ie_tipo_item,
				nr_prescricao,
				nr_seq_item,		
				cd_item,
				ds_item,
				ie_acm_sn,		
				cd_intervalo,
				qt_item,
				ds_prescricao,
				ds_diluicao,
				nr_agrupamento,
				ie_diferenciado,
				ie_pendente_liberacao,
				ie_status_item,
				nr_seq_cpoe,
				ie_item_ordem,
				nr_prescricoes)
			values (
				nr_seq_wadep_w,
				nm_usuario_p,
				'O',
				current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_gasoterapia_w,
				nr_seq_gas_w,
				substr(ds_gas_w,1,240),
				ie_acm_sn_w,
				cd_intervalo_w,
				qt_gasoterapia_w,
				ds_prescricao_w,
				ds_config_gas_w,
				0,
				'N',
				current_setting('adep_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_status_gas_w,
				nr_seq_gas_cpoe_w,
				ie_item_ordem_w,
				current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type);
			commit;
			end;
		end loop;
		close c02;

	elsif (getIeGasSeparada = 'N' and (ie_html_p = 'S' or (ie_java_p = 'S' and verificar_cpoe_ativa(cd_estabelecimento_p, nr_atendimento_p) = 'S'))) then

		for c03_w in c03 loop
			begin
			
			select	min(a.nr_prescricao)
			into STRICT	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type
			from	prescr_gasoterapia b,
					prescr_medica a
			where	a.nr_prescricao = b.nr_prescricao
			and		b.nr_seq_gas_cpoe = c03_w.nr_seq_gas_cpoe
			and		a.dt_inicio_prescr between dt_inicial_horarios_p and dt_final_horarios_p;

			if (c03_w.dt_suspensao_cpoe <= clock_timestamp()) then
				--Expressao 820376: (SUSPENSO)
				ds_gas_w := wheb_mensagem_pck.get_texto(820376) || ' ' || substr(c03_w.ds_gas,1,240);
			elsif (c03_w.dt_suspensao_cpoe > clock_timestamp()) then
				--1061446: Suspenso a partir de: #@DT_SUSPENSAO#@
				ds_gas_w := '(' || wheb_mensagem_pck.get_texto(1061446, 'DT_SUSPENSAO='
					|| TO_CHAR(c03_w.dt_suspensao_cpoe, pkg_date_formaters.localize_mask('short',
						pkg_date_formaters.getUserLanguageTag(cd_estabelecimento_p,
						nm_usuario_p)))) || ') ' || substr(c03_w.ds_gas,1,240);
			else
				ds_gas_w := substr(c03_w.ds_gas,1,240);
			end if;

			insert into w_adep_t(
				nr_sequencia,
				nm_usuario,
				ie_tipo_item,
				cd_item,
				ds_item,
				ie_acm_sn,
				cd_intervalo,
				qt_item,
				ds_prescricao,
				ds_diluicao,
				nr_agrupamento,
				ie_diferenciado,
				ie_pendente_liberacao,
				ie_status_item,
				nr_seq_cpoe,
				nr_prescricoes)
			values (
				nextval('w_adep_t_seq'),
				nm_usuario_p,
				'O',
				c03_w.nr_seq_gas,
				substr(ds_gas_w,1,240),
				c03_w.ie_acm_sn,
				c03_w.cd_intervalo,
				c03_w.qt_gasoterapia,
				c03_w.ds_prescricao,
				c03_w.ds_config_gas,
				0,
				'N',
				c03_w.ie_lib_pend_rep,
				c03_w.ie_status_item,
				c03_w.nr_seq_gas_cpoe,
				current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type);

			commit;
			end;
		end loop;
	end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_gerar_horarios_pck.adep_obter_gas ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_lib_pend_rep_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint, ie_gas_horario_p text, ie_html_p text, ie_java_p text) FROM PUBLIC;
