CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_obter_horarios_gas ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_solucoes_continuas_p text, ie_prescr_setor_p text, ie_formato_solucoes_p text, cd_setor_paciente_p bigint, ie_gas_horario_p text, ie_palm_p text) AS $body$
DECLARE

	dt_horario_w	timestamp;
	ie_recem_nato_w		varchar(1) := adep_gerar_horarios_pck.get_ie_recem_nato();	
	c01 CURSOR FOR
	SELECT	coalesce(x.dt_prev_execucao, a.dt_inicio_prescr)
	from	prescr_gasoterapia x,
		prescr_medica a
	where	x.nr_prescricao = a.nr_prescricao
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	((a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w) or
		((getItensGasTerm = 'S') and ((substr(Obter_Status_Gasoterapia(x.nr_sequencia, 'C'),1,80) in ('I','IN','R')) or
		((substr(Obter_Status_Gasoterapia(x.nr_sequencia, 'C'),1,80) = 'N') and (dt_inicial_horarios_p between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440)))) or (getItensGasTerm = 'T'))
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))
	and	(((coalesce(x.dt_prev_execucao::text, '') = '') or (x.dt_prev_execucao between dt_inicial_horarios_p and dt_final_horarios_p)) or
		((getItensGasTerm = 'S') and ((substr(Obter_Status_Gasoterapia(x.nr_sequencia, 'C'),1,80) in ('I','IN','R')) or
		((substr(Obter_Status_Gasoterapia(x.nr_sequencia, 'C'),1,80) = 'N') and (dt_inicial_horarios_p between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440)))) or (getItensGasTerm = 'T'))
	and	((ie_regra_inclusao_p = 'S') or
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'OX', 
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		null, 
																		null, 
																		null, 
																		null,
																		a.cd_setor_Atendimento,
																		null,
																		null,			-- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
																		null) = 'S')))	 -- nr_seq_exame_p
	and	((ie_exibir_hor_realizados_p = 'S') or ((ie_exibir_hor_realizados_p = 'N') and coalesce(substr(obter_status_gasoterapia(x.nr_sequencia, 'C'),1,10),'N') <> 'T'))
	and	((ie_exibir_hor_suspensos_p = 'S') or ((ie_exibir_hor_suspensos_p = 'N') and coalesce(substr(obter_status_gasoterapia(x.nr_sequencia, 'C'),1,10),'N') <> 'S'))
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))	
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	substr(Obter_Status_Gasoterapia(x.nr_sequencia, 'C'),1,80) = 'N'
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))	
	group by
		  coalesce(x.dt_prev_execucao, a.dt_inicio_prescr);
	c02 CURSOR FOR
	SELECT	c.dt_horario
	from	prescr_gasoterapia_hor c,
			prescr_gasoterapia x,
			prescr_medica a
	where	x.nr_prescricao = a.nr_prescricao
	and		x.nr_prescricao = c.nr_prescricao
	and		x.nr_sequencia  = c.nr_seq_gasoterapia
	and		obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and		a.nr_atendimento = nr_atendimento_p
	and		((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))
	and		(--(a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w) or
			 (c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p) or
			((getItensGasTerm = 'S') and ((substr(Obter_Status_Gasoterapia(x.nr_sequencia, 'C'),1,80) in ('I','IN','R')) or
			((substr(Obter_Status_Gasoterapia(x.nr_sequencia, 'C'),1,80) = 'N') and (dt_inicial_horarios_p between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440)))) or (getItensGasTerm = 'T'))
	and		((ie_regra_inclusao_p = 'S') or
			 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'OX',
																			cd_estabelecimento_p, 
																			cd_setor_usuario_p, 
																			cd_perfil_p, 
																			null, 
																			null, 
																			null, 
																			null,
																			a.cd_setor_Atendimento,
																			null,
																			null,			-- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
																			null) = 'S')))	 -- nr_seq_exame_p
	and (c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p)
	and	    ((ie_exibir_suspensos_p = 'S') or ((ie_exibir_suspensos_p = 'N') and (coalesce(x.dt_suspensao::text, '') = '')))																			
	and		((ie_exibir_hor_suspensos_p = 'S') or ((ie_exibir_hor_suspensos_p = 'N') and coalesce(substr(obter_status_etapa_gas(x.nr_sequencia, c.nr_etapa, c.dt_fim_horario, c.dt_suspensao),1,10),'N') <> 'S'))
	and		((ie_exibir_hor_realizados_p = 'S') or ((ie_exibir_hor_realizados_p = 'N') and coalesce(substr(obter_status_gasoterapia(x.nr_sequencia, 'C'),1,10),'N') <> 'T'))	
	and		((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))		
	and		((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	and 	coalesce(c.ie_horario_especial,'N') = 'N'		
	and		(((coalesce(ie_palm_p,'N') = 'S') and (coalesce(c.dt_interrupcao::text, '') = '') and (coalesce(c.dt_fim_horario::text, '') = '') ) or (coalesce(ie_palm_p,'N') = 'N'))
	and 	coalesce(c.ie_horario_especial,'N') = 'N'
	and		(c.dt_lib_horario IS NOT NULL AND c.dt_lib_horario::text <> '')	
	and ((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (coalesce(a.dt_suspensao::text, '') = '' and coalesce(x.dt_suspensao::text, '') = ''))
	and exists (	SELECT	1
				from	prescr_gasoterapia_hor b
				where	b.nr_prescricao = x.nr_prescricao
				and	b.nr_seq_gasoterapia = x.nr_sequencia
				and	(b.dt_lib_horario IS NOT NULL AND b.dt_lib_horario::text <> '')
				and	(b.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p 
					 or ((getItensGasTerm = 'S') and (substr(Obter_Status_Gasoterapia(x.nr_sequencia, 'C'),1,80) in ('I','IN','R')))))
	group by
			c.dt_horario;
	tb_datas_w	dbms_sql.date_table;
	
BEGIN
	if (ie_gas_horario_p = 'N') then

		open c01;
		loop
		fetch C01 bulk collect into tb_datas_w
		limit 100;
		exit when tb_datas_w.count = 0;
			begin
			forall i in tb_datas_w.first..tb_datas_w.last
				insert into w_adep_horarios_t(	nm_usuario,
												dt_horario)
											values (
												nm_usuario_p,
												tb_datas_w(i));
				commit;
			end;
		end loop;
		close c01;
	elsif (ie_gas_horario_p = 'S') then

		open c02;
		loop
		fetch C02 bulk collect into tb_datas_w
		limit 100;
		exit when tb_datas_w.count = 0;
			begin
			forall i in tb_datas_w.first..tb_datas_w.last
				insert into w_adep_horarios_t(	nm_usuario,
												dt_horario)
											values (
												nm_usuario_p,
												tb_datas_w(i));
				commit;
			end;
		end loop;
		close c02;
	end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_gerar_horarios_pck.adep_obter_horarios_gas ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_solucoes_continuas_p text, ie_prescr_setor_p text, ie_formato_solucoes_p text, cd_setor_paciente_p bigint, ie_gas_horario_p text, ie_palm_p text) FROM PUBLIC;
