CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_obter_horarios_medic ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_mostrar_substituido_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint, ie_quimio_p text, ie_palm_p text, ie_clinician_p text) AS $body$
DECLARE

	dt_horario_w	timestamp;
	ie_recem_nato_w		varchar(1) := adep_gerar_horarios_pck.get_ie_recem_nato();	
	c01 CURSOR FOR
	SELECT	c.dt_horario
	from	prescr_material x,
		prescr_mat_hor c,
		prescr_medica a
	where	x.nr_prescricao	= c.nr_prescricao
	and	x.nr_sequencia = c.nr_seq_material
	and	x.nr_prescricao = a.nr_prescricao
	and	c.nr_prescricao = a.nr_prescricao
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	coalesce(a.ie_hemodialise, 'N') <> 'R'
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	obter_se_prescr_lib_adep(coalesce(a.dt_liberacao_medico,x.dt_lib_material), coalesce(a.dt_liberacao,x.dt_lib_material), coalesce(a.dt_liberacao_farmacia,x.dt_lib_material), ie_data_lib_prescr_p, a.ie_lib_farm) = 'S'
	and	x.ie_agrupador = 1
	and	(((coalesce(ie_palm_p,'N') = 'S') and (coalesce(c.nr_seq_processo::text, '') = '') and (getFormaAdmPalm in ('H', 'PH'))) or
		  	  ((coalesce(ie_palm_p,'N') = 'S') and	((obter_status_processo_adep(c.nr_seq_processo) in ('P', 'L', 'R')) or
		  									     ((obter_status_processo_adep(c.nr_seq_processo) in ('G', 'D', 'H')) and 
											      ((getIeConsisteTempoProcesso = 'D') and (obter_se_cons_tempo_processo(cd_perfil_p, a.cd_setor_atendimento, c.nr_seq_processo) = 'N'))))) or (coalesce(ie_palm_p,'N') = 'N')or (coalesce(ie_clinician_p,'N') = 'S' and (obter_status_processo_adep(c.nr_seq_processo) = 'A') and (ie_exibir_hor_realizados_p = 'S')))
	/* and	(((Obter_se_acm_sn_agora_especial(x.ie_acm, x.ie_se_necessario, x.ie_urgencia, c.ie_dose_especial) = 'N') and 
			  (c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p)) or
			 ((Obter_se_acm_sn_agora_especial(x.ie_acm, x.ie_se_necessario, x.ie_urgencia, c.ie_dose_especial) = 'S') and 
			  ((obter_se_prescr_vig_adep(a.dt_prescricao,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S') or ((c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p))))) */
	and	c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p		  
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and	coalesce(c.ie_situacao,'A') = 'A'
	and	coalesce(c.ie_adep,'S') = 'S'
	and	coalesce(x.ie_administrar,'S') <> 'N'
	and	((ie_mostrar_substituido_p	= 'S') or (coalesce(x.nr_seq_substituto::text, '') = ''))
	and	c.ie_agrupador = 1
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))
	and	((coalesce(x.nr_seq_kit::text, '') = '') or ((getie_apres_kit = 'S') and (coalesce(ie_palm_p,'N') = 'N')))	
	and	((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
	and	((ie_regra_inclusao_p = 'S') or
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'MED', 
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		c.cd_material, 
																		null, 
																		null, 
																		null, 
																		a.cd_Setor_atendimento,
																		null, 
																		a.nr_prescricao,
																		null) = 'S'))) -- nr_seq_exame_p
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	and	((ie_quimio_p		= 'S') or (coalesce(a.nr_seq_atend::text, '') = ''))
	and	coalesce(c.ie_horario_adep,'S') = 'S'
	and not exists (SELECT 1 
					  from cpoe_material m,
						   cpoe_hemoterapia h
					 where m.nr_sequencia = x.nr_seq_mat_cpoe
					   and h.nr_sequencia = m.nr_seq_hemoterapia)	 		
	group by
		c.dt_horario;
	tb_datas_w	dbms_sql.date_table;
	
BEGIN
	open c01;
	loop
	fetch C01 bulk collect into tb_datas_w
	limit 100;
	exit when tb_datas_w.count = 0;
		begin
		forall i in tb_datas_w.first..tb_datas_w.last
			insert into w_adep_horarios_t(	nm_usuario,
											dt_horario,
											ie_medicamento)
										values (
											nm_usuario_p,
											tb_datas_w(i),
											'S');
			commit;
		end;
	end loop;
	close c01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_gerar_horarios_pck.adep_obter_horarios_medic ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_mostrar_substituido_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint, ie_quimio_p text, ie_palm_p text, ie_clinician_p text) FROM PUBLIC;
