SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_obter_horarios_sne ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_sol_realizadas_p text, ie_exibir_sol_suspensas_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint, ie_palm_p text) AS $body$
DECLARE

	dt_horario_w	timestamp;
	ie_utiliza_servico_w	varchar(1) := adep_gerar_horarios_pck.get_usa_servico_dietas();
	ie_recem_nato_w		varchar(1) := adep_gerar_horarios_pck.get_ie_recem_nato();	
	ie_sne_por_horario_w varchar(1) := adep_gerar_horarios_pck.get_ie_sne_por_horario();
	c01 CURSOR FOR
	SELECT	x.dt_status
	from	prescr_material x,
		prescr_medica a
	where	x.nr_prescricao = a.nr_prescricao
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w	
	and	((obter_se_prescr_lib_adep(coalesce(a.dt_liberacao_medico,x.dt_lib_material), coalesce(a.dt_liberacao,x.dt_lib_material), coalesce(a.dt_liberacao_farmacia,x.dt_lib_material), ie_data_lib_prescr_p, a.ie_lib_farm) = 'S') or
		((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
	and	x.ie_agrupador = 8
	and	x.ie_status = 'N'
	and	x.dt_status between dt_inicial_horarios_p and dt_final_horarios_p
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))	
	and	((ie_SNE_por_horario_w = 'S') or (x.ie_status in ('I','INT')) or
		 ((Obter_se_acm_sn_agora_especial(x.ie_acm, x.ie_se_necessario, x.ie_urgencia, x.ie_dose_espec_agora) = 'N') and (x.dt_status between dt_inicial_horarios_p and dt_final_horarios_p)) or
		 ((Obter_se_acm_sn_agora_especial(x.ie_acm, x.ie_se_necessario, x.ie_urgencia, x.ie_dose_espec_agora) = 'S') and (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S')))
	and	((ie_exibir_sol_realizadas_p = 'S') or (x.ie_status <> 'T'))
	and	((ie_exibir_sol_suspensas_p = 'S') or ((coalesce(x.dt_suspensao::text, '') = '') and (coalesce(x.ie_horario_susp,'N') <> 'S')))
	and 	((ie_utiliza_servico_w = 'N') or (get_servico_sem_dieta = 'S'))
	and	((ie_regra_inclusao_p = 'S') or
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SNE',
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		x.cd_material, 
																		null, 
																		null, 
																		null,
																		a.cd_setor_Atendimento,
																		null,
																		null,			-- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
																		null) = 'S')))	  -- nr_seq_exame_p
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))	
	group by
		x.dt_status
	
union all

	SELECT	b.DT_SERVICO
	from	prescr_material x,
		nut_atend_serv_dia b,
		prescr_medica a
	where	x.nr_prescricao = a.nr_prescricao
	and 	b.nr_prescricao = x.nr_prescricao
	and	b.nr_seq_material = x.nr_sequencia
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	--and	((obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p, a.ie_lib_farm) = 'S') or
	and	((obter_se_prescr_lib_adep(coalesce(a.dt_liberacao_medico,x.dt_lib_material), coalesce(a.dt_liberacao,x.dt_lib_material), coalesce(a.dt_liberacao_farmacia,x.dt_lib_material), ie_data_lib_prescr_p, a.ie_lib_farm) = 'S') or
		((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
	and	x.ie_agrupador = 8
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))	
	and	b.DT_SERVICO between dt_inicial_horarios_p and dt_final_horarios_p
	and	((ie_exibir_sol_realizadas_p = 'S') or (x.ie_status <> 'T'))
	and	((ie_exibir_sol_suspensas_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and 	ie_utiliza_servico_w = 'S'
	and	get_servico_sem_dieta = 'N'
	and	((ie_regra_inclusao_p = 'S') or
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SNE', 
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		x.cd_material, 
																		null, 
																		null, 
																		null,
																		a.cd_setor_Atendimento,
																		null,
																		null,			-- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
																		null) = 'S')))	 -- nr_seq_exame_p
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))	
	group by
		b.DT_SERVICO;
	C02 CURSOR FOR
	SELECT	h.dt_horario
	from	prescR_mat_hor h,
		prescr_material x,
		prescr_medica a
	where	x.nr_prescricao = a.nr_prescricao
	and 	x.nr_prescricao = h.nr_prescricao
	and	x.nr_Sequencia	= h.nr_Seq_material
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	--and	((obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p, a.ie_lib_farm) = 'S') or
	and	((obter_se_prescr_lib_adep(coalesce(a.dt_liberacao_medico,x.dt_lib_material), coalesce(a.dt_liberacao,x.dt_lib_material), coalesce(a.dt_liberacao_farmacia,x.dt_lib_material), ie_data_lib_prescr_p, a.ie_lib_farm) = 'S') or
		((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
	and	x.ie_agrupador = 8
	and	h.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p
	and	((ie_exibir_sol_realizadas_p = 'S') or (x.ie_status <> 'T'))
	and	coalesce(h.ie_adep,'S') = 'S'
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))	
	and	((coalesce(h.ie_horario_especial,'N') = 'N') or (h.dt_fim_horario IS NOT NULL AND h.dt_fim_horario::text <> ''))
	and	((ie_exibir_sol_suspensas_p = 'S') or (coalesce(h.dt_suspensao::text, '') = ''))
	and	((ie_regra_inclusao_p = 'S') or
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SNE',
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		x.cd_material, 
																		null, 
																		null, 
																		null,
																		a.cd_setor_Atendimento,
																		null,
																		null,			-- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
																		null) = 'S'))) -- nr_seq_exame_p
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	and	coalesce(h.ie_situacao,'A') = 'A'
	and	((ie_exibir_sol_realizadas_p = 'S') or (coalesce(h.dt_fim_horario::text, '') = ''))	
	and Obter_se_horario_liberado(h.dt_lib_horario, h.dt_horario) = 'S'		
	group by
		h.dt_horario;
	tb_datas_w	dbms_sql.date_table;
	
BEGIN
	if (ie_sne_por_horario_w = 'N') then
		open c01;
		loop
		fetch C01 bulk collect into tb_datas_w
		limit 100;
		exit when tb_datas_w.count = 0;
			begin
			forall i in tb_datas_w.first..tb_datas_w.last
				insert into w_adep_horarios_t(	nm_usuario,
												dt_horario)
											values (
												nm_usuario_p,
												tb_datas_w(i));
				commit;
			end;
		end loop;
		close c01;
	else
		open c02;
		loop
		fetch C02 bulk collect into tb_datas_w
		limit 100;
		exit when tb_datas_w.count = 0;
			begin
			forall i in tb_datas_w.first..tb_datas_w.last
				insert into w_adep_horarios_t(	nm_usuario,
												dt_horario)
											values (
												nm_usuario_p,
												tb_datas_w(i));
				commit;
			end;
		end loop;
		close c02;
	end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_gerar_horarios_pck.adep_obter_horarios_sne ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_sol_realizadas_p text, ie_exibir_sol_suspensas_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint, ie_palm_p text) FROM PUBLIC;
