SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_obter_horarios_solucao ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_sol_realizadas_p text, ie_exibir_sol_suspensas_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_solucoes_continuas_p text, ie_solucoes_interrompidas_p text, ie_prescr_setor_p text, ie_formato_solucoes_p text, cd_setor_paciente_p bigint, ie_quimio_p text, ie_palm_p text, ie_html_p text default 'N', dt_referencia_p timestamp default null, ie_clinician_p text default 'N') AS $body$
DECLARE

	dt_horario_w		timestamp;
	dt_inicio_w		timestamp;
	dt_inicio_solucao_w	timestamp;
	dt_fim_solucao_w	timestamp;	
	ie_recem_nato_w		varchar(1) := adep_gerar_horarios_pck.get_ie_recem_nato();	
	c01 CURSOR FOR
	SELECT	x.dt_status
	from	prescr_solucao x,
		prescr_medica a
	where	x.nr_prescricao = a.nr_prescricao
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	coalesce(a.ie_hemodialise, 'N') <> 'R'
	and	a.dt_validade_prescr between dt_inicio_w and dt_fim_w
	and	obter_se_prescr_lib_adep(coalesce(a.dt_liberacao_medico, x.dt_lib_material), coalesce(a.dt_liberacao, x.dt_lib_material), coalesce(a.dt_liberacao_farmacia, x.dt_lib_farmacia), ie_data_lib_prescr_p, a.ie_lib_farm) = 'S'
	and	coalesce(x.nr_seq_dialise::text, '') = ''
	and	x.ie_status = 'N'
	and	x.dt_status between dt_inicial_horarios_p and dt_final_horarios_p
	and	((ie_exibir_sol_realizadas_p = 'S') or (x.ie_status <> 'T'))
	and	((ie_exibir_sol_suspensas_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))
	and	((a.cd_funcao_origem = 2314 and ie_html_p = 'S') or (ie_solucoes_continuas_p = 'S' and ie_html_p = 'N') or (Obter_se_sol_continua2(a.nr_prescricao,x.nr_seq_solucao,x.ds_solucao,dt_inicial_horarios_p, dt_final_horarios_p, dt_inicio_solucao_w, dt_fim_solucao_w, null) = 'N'))
	and	((ie_solucoes_interrompidas_p = 'S') or (x.ie_status <> 'INT'))
	and	((ie_regra_inclusao_p = 'S') or
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SOL',
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		null, 
																		null, 
																		null, 
																		null,
																		a.cd_setor_Atendimento,
																		null,
																		null,			-- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
																		null) = 'S'))) -- nr_seq_exame
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	and	((ie_quimio_p = 'S') or (coalesce(a.nr_seq_atend::text, '') = ''))
	group by
		x.dt_status;
	c02 CURSOR FOR
	SELECT	c.dt_horario
	from	prescr_solucao x,
		prescr_mat_hor c,
		prescr_medica a
	where	x.nr_prescricao = c.nr_prescricao
	and	x.nr_seq_solucao = c.nr_seq_solucao
	and	x.nr_prescricao = a.nr_prescricao
	and	c.nr_prescricao = a.nr_prescricao
	and	((x.ie_status('I','INT')) or
		 ((Obter_se_acm_sn_agora_especial(x.ie_acm, x.ie_se_necessario, x.ie_urgencia, c.ie_etapa_especial) = 'N') and
		  ((coalesce(c.ie_horario_especial,'N') = 'N') and (c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p))) or
		 ((Obter_se_acm_sn_agora_especial(x.ie_acm, x.ie_se_necessario, x.ie_urgencia, c.ie_etapa_especial) = 'S') and (obter_se_prescr_vig_adep(CASE WHEN Get_Param652='P' THEN a.dt_prescricao  ELSE a.dt_inicio_prescr END ,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S')))
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	coalesce(a.ie_hemodialise, 'N') <> 'R'
	and	a.dt_validade_prescr between dt_inicio_solucao_w and  dt_fim_solucao_w
	and ((ie_html_p = 'S' and c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p) or (ie_html_p = 'N'))
	and	obter_se_prescr_lib_adep(coalesce(a.dt_liberacao_medico, x.dt_lib_material), coalesce(a.dt_liberacao, x.dt_lib_material), coalesce(a.dt_liberacao_farmacia, x.dt_lib_farmacia), ie_data_lib_prescr_p, a.ie_lib_farm) = 'S'
	and	coalesce(x.nr_seq_dialise::text, '') = ''
	and	((get_ie_exibeSol_filtro = 'N') or (obter_se_sol_vig_adep(a.nr_prescricao, x.ie_status, qt_horas_anteriores_w, qt_horas_adicionais_w, dt_referencia_p, x.dt_suspensao, x.dt_status) = 'S'))
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and	coalesce(c.ie_situacao,'A') = 'A'
	and	coalesce(c.ie_adep,'S') = 'S'
	and	c.ie_agrupador = 4
	and	(((coalesce(ie_palm_p,'N') = 'S') and (coalesce(c.nr_seq_processo::text, '') = '') and (getFormaAdmPalm in ('H', 'PH'))) or
		  	  ((coalesce(ie_palm_p,'N') = 'S') and	((obter_status_processo_adep(c.nr_seq_processo) in ('P', 'L', 'R')) or 
		  									     ((obter_status_processo_adep(c.nr_seq_processo) in ('G', 'D', 'H')) and 
											      ((getIeConsisteTempoProcesso = 'D') and (obter_se_cons_tempo_processo(cd_perfil_p, a.cd_setor_atendimento, c.nr_seq_processo) = 'N'))))) or (coalesce(ie_palm_p,'N') = 'N') or (coalesce(ie_clinician_p,'N') = 'S' and (obter_status_processo_adep(c.nr_seq_processo) = 'A') and (ie_exibir_sol_realizadas_p = 'S')))
	and	(((coalesce(ie_palm_p,'N') = 'S') and (coalesce(c.dt_inicio_horario::text, '') = '')) or (coalesce(ie_palm_p,'N') = 'N') or (ie_clinician_p='S'))
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))
	and	((coalesce(c.ie_horario_especial,'N') = 'N') or (x.ie_etapa_especial = 'S'))
	and	adep_obter_se_gerar_sol_hor(c.dt_horario, c.dt_inicio_horario, c.dt_fim_horario, c.dt_suspensao, dt_inicial_horarios_p, dt_final_horarios_p) = 'S'
	and	((ie_exibir_sol_realizadas_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_sol_suspensas_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
	--and	((ie_solucoes_continuas_p = 'S') or
		-- (obter_se_sol_continua2(a.nr_prescricao,x.nr_seq_solucao,x.ds_solucao,dt_inicial_horarios_p, dt_final_horarios_p, dt_inicio_solucao_w, dt_fim_solucao_w) = 'N'))
	and	((ie_solucoes_interrompidas_p = 'S') or (x.ie_status <> 'INT'))
	and	((ie_regra_inclusao_p = 'S') or
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SOL', 
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		null, 
																		null, 
																		null, 
																		null,
																		a.cd_setor_Atendimento,
																		null,
																		null,			-- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
																		null) = 'S'))) -- nr_seq_exame_p 
	and	((ie_prescr_setor_p = 'N') or 
		 (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	and	((ie_quimio_p		= 'S') or (coalesce(a.nr_seq_atend::text, '') = ''))
	and	coalesce(c.ie_horario_adep,'S') = 'S'
	and not exists (SELECT 1
					from prescr_material d
					where d.nr_prescricao	= c.nr_prescricao
					and	d.nr_sequencia = c.nr_seq_material
					and (d.nr_seq_substituto IS NOT NULL AND d.nr_seq_substituto::text <> ''))
	group by
		c.dt_horario;
	tb_datas_w	dbms_sql.date_table;
	
BEGIN
	dt_inicio_w	:= dt_validade_limite_p - 3;
	SELECT * FROM adep_gerar_horarios_pck.getdtrefsol(dt_validade_limite_p, dt_inicio_solucao_w, dt_fim_solucao_w) INTO STRICT dt_inicio_solucao_w, dt_fim_solucao_w;
	
	if (ie_formato_solucoes_p = 'I') then
		begin
		open c01;
		loop
		fetch C01 bulk collect into tb_datas_w
		limit 100;
		exit when tb_datas_w.count = 0;
			begin
			forall i in tb_datas_w.first..tb_datas_w.last
				insert into w_adep_horarios_t(	nm_usuario,
												dt_horario,
												ie_solucao)
											values (
												nm_usuario_p,
												tb_datas_w(i),
												'S');
				commit;
			end;
		end loop;
		close c01;
		end;
	elsif (ie_formato_solucoes_p = 'H') then
		begin
		open c02;
		loop
		fetch C02 bulk collect into tb_datas_w
		limit 100;
		exit when tb_datas_w.count = 0;
			begin
			forall i in tb_datas_w.first..tb_datas_w.last
				insert into w_adep_horarios_t(	nm_usuario,
												dt_horario,
												ie_solucao)
											values (
												nm_usuario_p,
												tb_datas_w(i),
												'S');
				commit;
			end;
		end loop;
		close c02;
		end;
	end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_gerar_horarios_pck.adep_obter_horarios_solucao ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_sol_realizadas_p text, ie_exibir_sol_suspensas_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_solucoes_continuas_p text, ie_solucoes_interrompidas_p text, ie_prescr_setor_p text, ie_formato_solucoes_p text, cd_setor_paciente_p bigint, ie_quimio_p text, ie_palm_p text, ie_html_p text default 'N', dt_referencia_p timestamp default null, ie_clinician_p text default 'N') FROM PUBLIC;
