CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_obter_jejum ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint ) AS $body$
DECLARE

	nr_seq_wadep_w	bigint;		
	cd_jejum_w	varchar(255);
	ds_jejum_w	varchar(255);
	ds_vigencia_w	varchar(255);
	ds_informacoes_w	varchar(2000);
	ie_suspenso_w varchar(1);
	ie_recem_nato_w		varchar(1) := adep_gerar_horarios_pck.get_ie_recem_nato();	
	nr_seq_dieta_cpoe_w	rep_jejum.nr_seq_dieta_cpoe%type;
	dt_suspensao_cpoe_w	timestamp;
	c01 CURSOR FOR
	SELECT	TO_CHAR(coalesce(c.dt_inicio,a.dt_inicio_prescr),'dd/mm/yyyy hh24:mi:ss') || '-' || TO_CHAR(CASE WHEN TO_CHAR(coalesce(c.dt_fim,a.dt_validade_prescr), 'mi')='00' THEN  coalesce(c.dt_fim,a.dt_validade_prescr) - 1/86400  ELSE coalesce(c.dt_fim,a.dt_validade_prescr) END ,'dd/mm/yyyy hh24:mi:ss') cd_jejum,
		x.ds_tipo ds_jejum,
		obter_desc_expressao(301700) || ': ' || to_char(c.dt_inicio,'dd/mm/yyyy hh24:mi:ss') || ' ' || obter_desc_expressao(347642) || ' ' || to_char(c.dt_fim,'dd/mm/yyyy hh24:mi:ss') ds_vigencia,
		coalesce(c.ie_suspenso,'N'),
		substr(CASE WHEN coalesce(c.ds_observacao::text, '') = '' THEN  null  ELSE /* Obs: */
 wheb_mensagem_pck.get_texto(334581) || c.ds_observacao END  || adep_gerar_horarios_pck.obter_ds_justificativa(obter_justificativa_item(c.nr_prescricao, c.nr_sequencia, 'J')),1,2000) ds_informacoes,
		c.nr_seq_dieta_cpoe
	from	rep_tipo_jejum x,
		rep_jejum c,
		prescr_medica a
	where	x.nr_sequencia = c.nr_seq_tipo
	and	c.nr_prescricao = a.nr_prescricao
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	((obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p, a.ie_lib_farm) = 'S') or
		((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
	and	(((c.dt_inicio between dt_inicial_horarios_p and dt_final_horarios_p) or (c.dt_fim between dt_inicial_horarios_p and dt_final_horarios_p)) or (dt_inicial_horarios_p between c.dt_inicio and c.dt_fim))
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))		
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(c.ie_suspenso,'N') = 'N'))
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	group by
		c.dt_inicio,
		c.dt_fim,
		TO_CHAR(coalesce(c.dt_inicio,a.dt_inicio_prescr),'dd/mm/yyyy hh24:mi:ss') || '-' || TO_CHAR(CASE WHEN TO_CHAR(coalesce(c.dt_fim,a.dt_validade_prescr), 'mi')='00' THEN  coalesce(c.dt_fim,a.dt_validade_prescr) - 1/86400  ELSE coalesce(c.dt_fim,a.dt_validade_prescr) END ,'dd/mm/yyyy hh24:mi:ss'),
		obter_desc_expressao(301700) || ': ' || to_char(c.dt_inicio,'dd/mm/yyyy hh24:mi:ss') || ' ' || obter_desc_expressao(347642) || ' ' || to_char(c.dt_fim,'dd/mm/yyyy hh24:mi:ss'),
		x.ds_tipo,
		c.ie_suspenso,
		substr(CASE WHEN coalesce(c.ds_observacao::text, '') = '' THEN  null  ELSE /* Obs: */
 wheb_mensagem_pck.get_texto(334581) || c.ds_observacao END  || adep_gerar_horarios_pck.obter_ds_justificativa(obter_justificativa_item(c.nr_prescricao, c.nr_sequencia, 'J')),1,2000),
		c.nr_seq_dieta_cpoe;
	
BEGIN
	open c01;
	loop
	fetch c01 into	cd_jejum_w,
			ds_jejum_w,
			ds_vigencia_w,
			ie_suspenso_w,
			ds_informacoes_w,
			nr_seq_dieta_cpoe_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		--Jejum
		dt_suspensao_cpoe_w := cpoe_obter_dt_suspensao(nr_seq_dieta_cpoe_w, 'N');
		if	dt_suspensao_cpoe_w <= clock_timestamp() then
			ds_jejum_w := wheb_mensagem_pck.get_texto(820376) || ' ' || substr(ds_jejum_w,1,240);
		elsif (dt_suspensao_cpoe_w > clock_timestamp()) then
			ds_jejum_w := '(' || wheb_mensagem_pck.get_texto(1061446, 'DT_SUSPENSAO=' || TO_CHAR(dt_suspensao_cpoe_w, pkg_date_formaters.localize_mask('short', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))) || ') ' || substr(ds_jejum_w,1,240);
		end if;
		select	nextval('w_adep_t_seq')
		into STRICT	nr_seq_wadep_w
		;
		insert into w_adep_t(
			nr_sequencia,
			nm_usuario,
			ie_tipo_item,
			cd_item,
			ds_item,
			ds_prescricao,
			nr_agrupamento,
			nr_seq_proc_interno,
			ie_acm_sn,
			ie_status_item,
			ds_diluicao,
			nr_seq_cpoe)
		values (
			nr_seq_wadep_w,
			nm_usuario_p,
			'J',
			cd_jejum_w,
			substr(ds_jejum_w,1,240),
			ds_vigencia_w,
			0,
			0,
			'N',
			ie_suspenso_w,
			ds_informacoes_w,
			nr_seq_dieta_cpoe_w);
		commit;
		end;
	end loop;
	close c01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
