			nr_agrup_prot_w,
			ds_via_aplicacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		if (coalesce(nr_agrup_prot_w::text, '') = '') then
			nr_agrup_prot_w	:= 999;
		end if;

		if (nr_seq_mat_cpoe_w > 0) then
			begin
			select	max(ie_dose_ataque),
					max(ie_dose_adicional)
			into STRICT	ie_dose_ataque_w,
					ie_dose_adicional_w
			from	cpoe_material
			where	nr_sequencia = nr_seq_mat_cpoe_w;
			
			if (ie_dose_ataque_w = 'S') then
				ds_prescricao_w	:= obter_desc_expressao(330260) || ' ' || ds_prescricao_w || ' ' || ds_via_aplicacao_w;
			elsif (ie_dose_adicional_w = 'S') then
				ds_prescricao_w	:= obter_desc_expressao(656390) || ' ' || ds_prescricao_w || ' ' || ds_via_aplicacao_w;
			else
				ds_prescricao_w	:= obter_desc_expressao(288237) || ' ' || ds_prescricao_w || ' ' || ds_via_aplicacao_w;
			end if;
			
			end;
		else
			ds_prescricao_w	:= obter_desc_expressao(288237) || ' ' || ds_prescricao_w;
		end if;
		select	nextval('w_adep_t_seq')
		into STRICT	nr_seq_wadep_w
		;
		insert into w_adep_t(
			nr_sequencia,
			nm_usuario,
			ie_tipo_item,
			nr_prescricao,
			nr_seq_item,		
			cd_item,
			ds_item,
			ie_acm_sn,		
			cd_intervalo,
			qt_item,
			nr_agrupamento,
			ds_prescricao,
			ds_diluicao,
			ie_status_item,
			nr_seq_proc_interno,
			ie_diferenciado,
			nr_prescricoes,
			ie_via_aplicacao,
			ie_alto_risco,
			ie_quimio,
			ie_medicacao_paciente,
			ie_risco_queda,
			nr_agrup_prot,
			nr_seq_cpoe)
		values (
			nr_seq_wadep_w,
			nm_usuario_p,
			'M',
			current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
			nr_seq_material_w,
			cd_material_w,
			substr(ds_material_w,1,240),
			ie_acm_sn_w,
			cd_intervalo_w,
			qt_dose_w,
			nr_agrupamento_w,
			ds_prescricao_w,
			ds_dil_obs_w,
			ie_status_w,
			0,
			CASE WHEN coalesce(ds_dil_obs_w::text, '') = '' THEN 'N'  ELSE 'S' END ,
			CASE WHEN ie_acm_sn_w='S' THEN current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type  ELSE null END ,
			ie_via_aplicacao_w,
			ie_alto_risco_w,
			ie_quimio_w,
			ie_medicacao_paciente_w,
			ie_risco_queda_w,
			nr_agrup_prot_w,
			nr_seq_mat_cpoe_w);
		commit;
		end;
	end loop;
	close c01;
	if (ie_lib_pend_rep_p = 'S') then
		begin
		open c02;
		loop
		fetch c02 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_material_w,
				cd_material_w,
				ds_material_w,
				ie_acm_sn_w,		
				cd_intervalo_w,
				qt_dose_w,
				nr_agrupamento_w,
				ds_prescricao_w,
				ds_dil_obs_w,
				ie_status_w,
				current_setting('adep_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_horario_w,
				ie_via_aplicacao_w,
				ie_alto_risco_w,
				ie_quimio_w,
				ie_risco_queda_w,
				nr_seq_mat_cpoe_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			select	nextval('w_adep_t_seq')
			into STRICT	nr_seq_wadep_w
			;
			insert into w_adep_t(
				nr_sequencia,
				nm_usuario,
				ie_tipo_item,
				nr_prescricao,
				nr_seq_item,		
				cd_item,
				ds_item,
				ie_acm_sn,		
				cd_intervalo,
				qt_item,
				nr_agrupamento,
				ds_prescricao,
				ds_diluicao,
				ie_status_item,
				nr_seq_proc_interno,
				ie_diferenciado,
				nr_prescricoes,
				ie_pendente_liberacao,
				ie_horario,
				ie_via_aplicacao,
				ie_alto_risco,
				ie_quimio,
				ie_risco_queda,
				nr_seq_cpoe)
			values (
				nr_seq_wadep_w,
				nm_usuario_p,
				'M',
				current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_material_w,
				cd_material_w,
				substr(ds_material_w,1,240),
				ie_acm_sn_w,
				cd_intervalo_w,
				qt_dose_w,
				nr_agrupamento_w,
				ds_prescricao_w,
				ds_dil_obs_w,
				ie_status_w,
				0,
				CASE WHEN coalesce(ds_dil_obs_w::text, '') = '' THEN 'N'  ELSE 'S' END ,
				CASE WHEN ie_acm_sn_w='S' THEN current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type  ELSE null END ,
				current_setting('adep_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_horario_w,
				ie_via_aplicacao_w,
				ie_alto_risco_w,
				ie_quimio_w,
				ie_risco_queda_w,
				nr_seq_mat_cpoe_w);
			commit;
			end;
		end loop;
		close c02;	
		end;
	end if;
	END;

$body$
