CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_obter_medic ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_lib_pend_rep_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_agrupar_dose_esp_p text, ie_diluicao_acm_sn_gestao_p text, ie_mostrar_substituido_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint, ie_agrupa_suspensos_p text, ie_palm_p text, ie_quimio_p text, ie_clinician_p text, ie_html_p text default 'N') AS $body$
