	ie_prescr_anest_w			varchar(1);
	c01 CURSOR FOR
	SELECT	CASE WHEN coalesce(nr_seq_mat_cpoe::text, '') = '' THEN  CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupa_acm_sn_medic(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p,dt_inicial_horarios_p,dt_final_horarios_p)='N' THEN  nr_prescricao  ELSE null END   ELSE null END   ELSE null END ,
			CASE WHEN coalesce(nr_seq_mat_cpoe::text, '') = '' THEN  CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupa_acm_sn_medic(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p,dt_inicial_horarios_p,dt_final_horarios_p)='N' THEN  nr_seq_material  ELSE null END   ELSE null END   ELSE null END ,
			cd_material,
			ds_material,
			ie_acm_sn,
			cd_intervalo,
			qt_dose,
			nr_agrupamento,
			ds_prescricao,
			CASE WHEN ie_agrupa_suspensos_p='N' THEN  CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupa_acm_sn_medic(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p,dt_inicial_horarios_p,dt_final_horarios_p)='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE coalesce(ie_suspenso, 'N') END   ELSE coalesce(ie_suspenso, 'N') END   ELSE coalesce(ie_suspenso, 'N') END  ie_status,
			ie_via_aplicacao,
			ie_medicacao_paciente,
			CASE WHEN get_ie_obs_medico='S' THEN  ie_diluido  ELSE null END  ie_diluido,
			CASE WHEN get_ie_obs_medico='S' THEN  ie_obs_medico  ELSE null END  ie_obs_medico,
			CASE WHEN ie_acm_sn='S' THEN  CASE WHEN coalesce(cd_motivo_baixa,0)=0 THEN  null  ELSE CASE WHEN obter_se_aprazou_hor(nr_prescricao, nr_seq_material, 'M')='S' THEN  cd_motivo_baixa  ELSE null END  END   ELSE cd_motivo_baixa END ,
			ie_composto,
			ie_sol_custo,
			ie_alto_risco,
			CASE WHEN coalesce(nr_seq_atend::text, '') = '' THEN 'N'  ELSE 'S' END  ie_quimio,
			cd_unidade_medida_dose,
			ie_padronizado,
			nr_agrup_prot,
			ds_observacao,
