			ie_regra_disp,
			nr_seq_mat_cpoe,
			ie_risco_queda,
			ie_prescr_anest
	from	(
		SELECT	a.nr_prescricao,
			c.nr_seq_material,
			c.cd_material,
			substr(VIPE_obter_se_medic_composto(a.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, 'D')  
             || CASE WHEN coalesce(x.ie_lado, '')='N' THEN  '' WHEN coalesce(x.ie_lado, '')='' THEN  ''  ELSE ' (' || obter_desc_expressao(292416) || ': ' || obter_valor_dominio(1372,x.ie_lado) || ')' END ,1,240) ds_material,
			obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,
			x.cd_intervalo,
			CASE WHEN coalesce(ie_palm_p,'N')='S' THEN CASE WHEN coalesce(c.ie_dose_especial,'N')='S' THEN c.qt_dose  ELSE CASE WHEN coalesce(x.qt_dose,0)=0 THEN c.qt_dose  ELSE x.qt_dose END  END   ELSE x.qt_dose END  qt_dose,
			CASE WHEN obter_se_agrupa_composto(a.nr_prescricao,c.nr_seq_material,x.nr_agrupamento,c.cd_material)='S' THEN  					coalesce(obter_agrupamento_composto(a.nr_prescricao, c.cd_material, CASE WHEN obter_se_rep_cpoe(a.nr_prescricao)='S' THEN  x.nr_seq_mat_cpoe  ELSE null END ), x.nr_agrupamento)  ELSE 0 END  nr_agrupamento,
			substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,250) ds_prescricao,
			coalesce(x.ie_suspenso,'N') ie_suspenso,
			x.ie_via_aplicacao,
			coalesce(x.ie_medicacao_paciente,'N') ie_medicacao_paciente,
			substr(obter_se_material_diluido(x.nr_prescricao, x.nr_sequencia),1,5) ie_diluido,
			CASE WHEN trim(both coalesce(x.ds_observacao,x.ds_observacao_enf)) IS NULL THEN ''  ELSE 'S' END  ie_obs_medico,
			CASE WHEN get_usa_legenda_item_baixado='S' THEN x.cd_motivo_baixa  ELSE null END  cd_motivo_baixa,
			VIPE_obter_se_medic_composto(a.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, 'A') ie_composto,			
			coalesce(obter_se_mat_alto_custo(y.cd_material, cd_estabelecimento_p), 'N') ie_sol_custo,
			CASE WHEN coalesce(Obter_se_destaca_risco(a.cd_setor_atendimento),'S')='S' THEN  coalesce(obter_se_mat_alto_risco(a.nr_prescricao, x.nr_agrupamento, x.nr_sequencia),'S')  ELSE 'N' END  ie_alto_risco,
			a.nr_seq_atend,
			x.cd_unidade_medida_dose,
			Obter_se_material_padronizado(cd_estabelecimento_p,y.cd_material) ie_padronizado,
			CASE WHEN VarOrdenaProtocolo='S' THEN  CASE WHEN coalesce(a.nr_seq_atend,0)=0 THEN null  ELSE Obter_agrup_protocolo_medic(x.NR_SEQ_PROTOCOLO,x.cd_protocolo,x.nr_seq_mat_protocolo) END   ELSE null END  nr_agrup_prot,
			substr(x.ds_observacao,1,2000) ds_observacao,
			CASE WHEN ie_palm_p='S' THEN obter_se_setor_oncologia(a.cd_setor_atendimento)  ELSE null END  ie_oncologia,
