			Obter_medic_risco_queda(c.cd_material, cd_estabelecimento_p, cd_perfil_p, cd_setor_usuario_p, nm_usuario_p, nr_atendimento_p) ie_risco_queda,
			obter_valor_prescr_medica_comp(a.nr_prescricao,'IE_PRESCRICAO_ANESTESICA') ie_prescr_anest
		from	material y,
			prescr_material x,
			prescr_mat_hor c,
