		and	((ie_quimio_p		= 'S') or (coalesce(a.nr_seq_atend::text, '') = ''))
		and not exists (select 1 
  						  from cpoe_material m,
       						   cpoe_hemoterapia h
 						 where m.nr_sequencia = x.nr_seq_mat_cpoe
   						   and h.nr_sequencia = m.nr_seq_hemoterapia)	 
		group by
