			x.cd_unidade_medida_dose,
			Obter_se_material_padronizado(cd_estabelecimento_p,y.cd_material),
			substr(x.ds_observacao,1,2000),
			CASE WHEN ie_palm_p='S' THEN obter_se_setor_oncologia(a.cd_setor_atendimento)  ELSE null END ,
			CASE WHEN x.ie_regra_disp='E' THEN 'E'  ELSE null END ,
