			Obter_medic_risco_queda(c.cd_material, cd_estabelecimento_p, cd_perfil_p, cd_setor_usuario_p, nm_usuario_p, nr_atendimento_p),
			obter_valor_prescr_medica_comp(a.nr_prescricao,'IE_PRESCRICAO_ANESTESICA'),
			substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,250)
		) alias180
	group by
		CASE WHEN coalesce(nr_seq_mat_cpoe::text, '') = '' THEN  CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupa_acm_sn_medic(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p,dt_inicial_horarios_p,dt_final_horarios_p)='N' THEN  nr_prescricao  ELSE null END   ELSE null END   ELSE null END ,
		CASE WHEN coalesce(nr_seq_mat_cpoe::text, '') = '' THEN  CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupa_acm_sn_medic(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p,dt_inicial_horarios_p,dt_final_horarios_p)='N' THEN  nr_seq_material  ELSE null END   ELSE null END   ELSE null END ,
		cd_material,
		ds_material,
		ie_acm_sn,
		cd_intervalo,
		qt_dose,
		nr_agrupamento,
		ds_prescricao,
		CASE WHEN ie_agrupa_suspensos_p='N' THEN  CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupa_acm_sn_medic(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p,dt_inicial_horarios_p,dt_final_horarios_p)='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE coalesce(ie_suspenso, 'N') END   ELSE coalesce(ie_suspenso, 'N') END   ELSE coalesce(ie_suspenso, 'N') END ,
		ie_via_aplicacao,
		nr_agrup_prot,
		ie_medicacao_paciente,
		CASE WHEN get_ie_obs_medico='S' THEN  ie_diluido  ELSE null END ,
		CASE WHEN get_ie_obs_medico='S' THEN  ie_obs_medico  ELSE null END ,
		CASE WHEN ie_acm_sn='S' THEN  CASE WHEN coalesce(cd_motivo_baixa,0)=0 THEN  null  ELSE CASE WHEN obter_se_aprazou_hor(nr_prescricao, nr_seq_material, 'M')='S' THEN  cd_motivo_baixa  ELSE null END  END   ELSE cd_motivo_baixa END ,
		CASE WHEN coalesce(nr_seq_atend::text, '') = '' THEN 'N'  ELSE 'S' END ,
		ie_composto,
		ie_sol_custo, 
		ie_alto_risco,
		cd_unidade_medida_dose,
		ie_padronizado,
		ds_observacao,
		ie_oncologia,
		ie_regra_disp,
		nr_seq_mat_cpoe,
		ie_risco_queda,
		ie_prescr_anest;
	c02 CURSOR FOR
	SELECT	CASE WHEN coalesce(nr_seq_mat_cpoe::text, '') = '' THEN  nr_prescricao  ELSE null END ,
			CASE WHEN coalesce(nr_seq_mat_cpoe::text, '') = '' THEN  nr_seq_material  ELSE null END ,
			cd_material,
			ds_material,
			ie_acm_sn,
			cd_intervalo,
			qt_dose,
			nr_agrupamento,
			ds_prescricao,
			CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupa_acm_sn_medic(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p,dt_inicial_horarios_p,dt_final_horarios_p)='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE null END  WHEN ie_acm_sn='N' THEN  CASE WHEN ie_agrupa_suspensos_p='N' THEN  ie_suspenso  ELSE null END   ELSE null END  ie_status,
			ie_lib_pend_rep,
			ie_horario,
			ie_via_aplicacao,
			ie_medicacao_paciente,
			ie_diluido,
			ie_obs_medico,
			cd_motivo_baixa,
			CASE WHEN ie_suspenso='S' THEN null  ELSE ie_composto END  ie_composto,
			ie_sol_custo,
			ie_alto_risco,
			CASE WHEN coalesce(nr_seq_atend::text, '') = '' THEN 'N'  ELSE 'S' END  ie_quimio,
			nr_agrup_prot,
			ds_observacao,
			ie_oncologia,
			ie_regra_disp,
			nr_seq_mat_cpoe,
			ie_risco_queda,
			ie_prescr_anest
	from	(
		SELECT	a.nr_prescricao,
			x.nr_sequencia nr_seq_material,
			x.cd_material,
			substr(VIPE_obter_se_medic_composto(a.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, 'D')
            || CASE WHEN coalesce(x.ie_lado, '')='N' THEN  '' WHEN coalesce(x.ie_lado, '')='' THEN  ''  ELSE ' (' || obter_desc_expressao(292416) || ': ' || obter_valor_dominio(1372,x.ie_lado) || ')' END ,1,240) ds_material,
			obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,
			x.cd_intervalo,
			x.qt_dose qt_dose,
			CASE WHEN obter_se_agrupa_composto(a.nr_prescricao,x.nr_sequencia,x.nr_agrupamento,x.cd_material)='S' THEN x.nr_agrupamento  ELSE 0 END  nr_agrupamento,
			substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,x.nr_sequencia,x.ie_acm,x.ie_se_necessario),1,250) ds_prescricao,
			coalesce(x.ie_suspenso,'N') ie_suspenso,
			substr(adep_obter_lib_pend_rep_gestao(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia),1,1) ie_lib_pend_rep,
			CASE WHEN coalesce(x.ds_horarios::text, '') = '' THEN  'N'  ELSE 'S' END  ie_horario,
			x.ie_via_aplicacao,
			x.ie_medicacao_paciente,
			substr(obter_se_material_diluido(x.nr_prescricao, x.nr_sequencia),1,5) ie_diluido,
			CASE WHEN trim(both coalesce(x.ds_observacao,x.ds_observacao_enf)) IS NULL THEN ''  ELSE 'S' END  ie_obs_medico,
			CASE WHEN get_usa_legenda_item_baixado='S' THEN x.cd_motivo_baixa  ELSE null END  cd_motivo_baixa,
			VIPE_obter_se_medic_composto(a.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, 'A') ie_composto,			
			coalesce(obter_se_mat_alto_custo(y.cd_material, cd_estabelecimento_p), 'N') ie_sol_custo,
			CASE WHEN coalesce(Obter_se_destaca_risco(a.cd_setor_atendimento),'S')='S' THEN  coalesce(obter_se_mat_alto_risco(a.nr_prescricao, x.nr_agrupamento, x.nr_sequencia),'S')  ELSE 'N' END  ie_alto_risco,
			a.nr_seq_atend,
			CASE WHEN VarOrdenaProtocolo='S' THEN  CASE WHEN coalesce(a.nr_seq_atend,0)=0 THEN null  ELSE Obter_agrup_protocolo_medic(x.NR_SEQ_PROTOCOLO,x.cd_protocolo,x.nr_seq_mat_protocolo) END   ELSE null END  nr_agrup_prot,
			substr(x.ds_observacao,1,2000) ds_observacao,
			CASE WHEN ie_palm_p='S' THEN obter_se_setor_oncologia(a.cd_setor_atendimento)  ELSE null END  ie_oncologia,
			CASE WHEN x.ie_regra_disp='E' THEN 'E'  ELSE null END  ie_regra_disp,
			x.nr_seq_mat_cpoe,
			Obter_medic_risco_queda(x.cd_material, cd_estabelecimento_p, cd_perfil_p, cd_setor_usuario_p, nm_usuario_p, nr_atendimento_p) ie_risco_queda,
			obter_valor_prescr_medica_comp(a.nr_prescricao,'IE_PRESCRICAO_ANESTESICA') ie_prescr_anest
		from	material y,
			prescr_material x,
			prescr_medica a
		where	y.cd_material = x.cd_material
		and	x.nr_prescricao = a.nr_prescricao
		and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
		and	a.nr_atendimento = nr_atendimento_p
		and	coalesce(a.ie_hemodialise, 'N') <> 'R'
		and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
		and	(a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '')
		and	((coalesce(x.nr_seq_kit::text, '') = '') or ((getie_apres_kit = 'S') and (coalesce(ie_palm_p,'N') = 'N')))
		and	((coalesce(a.dt_liberacao::text, '') = '') or (coalesce(a.dt_liberacao_farmacia::text, '') = ''))
		and	x.ie_agrupador = 1
		and	((ie_mostrar_substituido_p	= 'S') or (coalesce(x.nr_seq_substituto::text, '') = ''))
		and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
		and	obter_se_gerar_item_adep('M',a.nr_prescricao,x.nr_sequencia,x.nr_agrupamento,getie_apres_kit) = 'S'
		and 	coalesce(x.dt_suspensao::text, '') = ''
		and 	coalesce(a.dt_suspensao::text, '') = ''	
		and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w		= 'S'))			
		and	((ie_agrupar_dose_esp_p = 'S') or (coalesce(x.ie_dose_espec_agora,'N') = 'N'))
		and 	coalesce(x.ie_Administrar,'S') = 'S'
		and	obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S'
		and	((ie_regra_inclusao_p = 'S') or
			 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'MED', 
																			cd_estabelecimento_p, 
																			cd_setor_usuario_p, 
																			cd_perfil_p, 
																			x.cd_material, 
																			null, 
																			null, 
																			null,
																			a.cd_setor_Atendimento,
																			null, 
																			a.nr_prescricao,
																			null) = 'S'))) -- nr_seq_exame_p
		and	not exists (
					select	1
					from	prescr_mat_hor c
					where	c.cd_material = y.cd_material
					and	c.nr_prescricao = x.nr_prescricao
					and	c.nr_seq_material = x.nr_sequencia
					and	c.ie_agrupador = 1
					and	x.ie_agrupador = 1
					and	c.nr_prescricao = a.nr_prescricao
					and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S')
		and	coalesce(x.dt_suspensao::text, '') = ''
		and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
		and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
		and	((ie_quimio_p		= 'S') or (coalesce(a.nr_seq_atend::text, '') = ''))		
		and not exists (select 1 
  						  from cpoe_material m,
       						   cpoe_hemoterapia h
 						 where m.nr_sequencia = x.nr_seq_mat_cpoe
   						   and h.nr_sequencia = m.nr_seq_hemoterapia)	 			 
		group by
			a.nr_prescricao,
			x.nr_sequencia,
			x.cd_material,
			substr(VIPE_obter_se_medic_composto(a.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, 'D') || CASE WHEN coalesce(x.ie_lado, '')='N' THEN  '' WHEN coalesce(x.ie_lado, '')='' THEN  ''  ELSE ' (' || obter_desc_expressao(292416) || ': ' || obter_valor_dominio(1372,x.ie_lado) || ')' END ,1,240),
			a.nr_seq_atend,
			x.ie_acm,
			x.ie_se_necessario,
			x.cd_intervalo,
			x.qt_dose,
			x.nr_agrupamento,
			x.ie_suspenso,
			a.dt_liberacao_medico,
			a.dt_liberacao,
			a.dt_liberacao_farmacia,
			x.ds_horarios,
			x.ie_via_aplicacao,
			CASE WHEN VarOrdenaProtocolo='S' THEN  CASE WHEN coalesce(a.nr_seq_atend,0)=0 THEN null  ELSE Obter_agrup_protocolo_medic(x.NR_SEQ_PROTOCOLO,x.cd_protocolo,x.nr_seq_mat_protocolo) END   ELSE null END ,
			x.ie_medicacao_paciente,
			substr(obter_se_material_diluido(x.nr_prescricao, x.nr_sequencia),1,5),
			CASE WHEN trim(both coalesce(x.ds_observacao,x.ds_observacao_enf)) IS NULL THEN ''  ELSE 'S' END ,
			x.cd_motivo_baixa,
			VIPE_obter_se_medic_composto(a.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, 'A'),			
			coalesce(obter_se_mat_alto_custo(y.cd_material, cd_estabelecimento_p), 'N'),
			CASE WHEN coalesce(Obter_se_destaca_risco(a.cd_setor_atendimento),'S')='S' THEN  coalesce(obter_se_mat_alto_risco(a.nr_prescricao, x.nr_agrupamento, x.nr_sequencia),'S')  ELSE 'N' END ,
			substr(x.ds_observacao,1,2000),
			CASE WHEN ie_palm_p='S' THEN obter_se_setor_oncologia(a.cd_setor_atendimento)  ELSE null END ,
			CASE WHEN x.ie_regra_disp='E' THEN 'E'  ELSE null END ,
			x.nr_seq_mat_cpoe,
			Obter_medic_risco_queda(x.cd_material, cd_estabelecimento_p, cd_perfil_p, cd_setor_usuario_p, nm_usuario_p, nr_atendimento_p),
			obter_valor_prescr_medica_comp(a.nr_prescricao,'IE_PRESCRICAO_ANESTESICA'),
			substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,x.nr_sequencia,x.ie_acm,x.ie_se_necessario),1,250)
		) alias118
	group by
		CASE WHEN coalesce(nr_seq_mat_cpoe::text, '') = '' THEN  nr_prescricao  ELSE null END ,
		CASE WHEN coalesce(nr_seq_mat_cpoe::text, '') = '' THEN  nr_seq_material  ELSE null END ,
		cd_material,
		ds_material,
		nr_agrup_prot,
		ie_acm_sn,
		cd_intervalo,
		qt_dose,
		nr_agrupamento,
		ds_prescricao,
		CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupa_acm_sn_medic(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p,dt_inicial_horarios_p,dt_final_horarios_p)='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE null END  WHEN ie_acm_sn='N' THEN  CASE WHEN ie_agrupa_suspensos_p='N' THEN  ie_suspenso  ELSE null END   ELSE null END ,
		ie_lib_pend_rep,
		ie_horario,
		ie_via_aplicacao,
		ie_medicacao_paciente,
		ie_diluido,
		ie_obs_medico,
		cd_motivo_baixa,
		CASE WHEN coalesce(nr_seq_atend::text, '') = '' THEN 'N'  ELSE 'S' END ,
		CASE WHEN ie_suspenso='S' THEN null  ELSE ie_composto END ,
		ie_sol_custo,  
		ie_alto_risco,
		ds_observacao,
		ie_oncologia,
		ie_regra_disp,
		nr_seq_mat_cpoe,
		ie_risco_queda,
		ie_prescr_anest;
	
BEGIN
	open c01;
	loop
	fetch c01 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
			nr_seq_material_w,
			cd_material_w,
			ds_material_w,
			ie_acm_sn_w,
			cd_intervalo_w,
			qt_dose_w,
			nr_agrupamento_w,
			ds_prescricao_w,
			ie_status_w,
			ie_via_aplicacao_w,
			ie_medicacao_paciente_w,
			ie_diluido_w,
			current_setting('adep_gerar_horarios_pck.ie_obs_medico_w')::varchar(5),
			cd_motivo_baixa_w,
			ie_composto_w,
			ie_sol_custo_w, ---Le
			ie_alto_risco_w,
			ie_quimio_w,
			cd_unidade_medida_dose_w,
			ie_padronizado_w,
			nr_agrup_prot_w,
			ds_observacao_w,
			ie_oncologia_w,
			ie_regra_disp_w,
			nr_seq_mat_cpoe_w,
			ie_risco_queda_w,
			ie_prescr_anest_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		select	nextval('w_adep_t_seq')
		into STRICT	nr_seq_wadep_w
		;
		if (coalesce(nr_agrup_prot_w::text, '') = '') then
			nr_agrup_prot_w	:= 999;
		end if;

		--Medic
		dt_suspensao_cpoe_w := cpoe_obter_dt_suspensao(nr_seq_mat_cpoe_w, 'M');
		if	dt_suspensao_cpoe_w <= clock_timestamp() then
			ds_material_w := wheb_mensagem_pck.get_texto(820376) || ' ' || substr(ds_material_w,1,240);
		elsif (dt_suspensao_cpoe_w > clock_timestamp()) then
			ds_material_w := '(' || wheb_mensagem_pck.get_texto(1061446, 'DT_SUSPENSAO=' || TO_CHAR(dt_suspensao_cpoe_w, pkg_date_formaters.localize_mask('short', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))) || ') ' || substr(ds_material_w,1,240);
		end if;

		insert into w_adep_t(
			nr_sequencia,
			nm_usuario,
			ie_tipo_item,
			nr_prescricao,
			nr_seq_item,
			cd_item,
			ds_item,
			ie_acm_sn,
			cd_intervalo,
			qt_item,
			nr_agrupamento,
			ds_prescricao,
			ie_status_item,
			nr_seq_proc_interno,
			nr_prescricoes,
			ie_via_aplicacao,
			ie_medicacao_paciente,
			ie_diluido,
			ie_obs_medico,
			cd_motivo_baixa,
			ie_composto,
			ie_sol_custo, --Le
			ie_alto_risco,
			ie_quimio,
			cd_unidade_medida,
			ie_padronizado,
			nr_agrup_prot,
			ds_observacao,
			ie_oncologia,
			ie_regra_disp,
			nr_seq_cpoe,
			ie_risco_queda,
			ie_prescricao_anestesica)
		values (
			nr_seq_wadep_w,
			nm_usuario_p,
			'M',
			current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
			nr_seq_material_w,
			cd_material_w,
			substr(ds_material_w,1,240),
			ie_acm_sn_w,
			cd_intervalo_w,
			qt_dose_w,
			nr_agrupamento_w,
			ds_prescricao_w,
			ie_status_w,
			0,
			CASE WHEN ie_acm_sn_w='S' THEN current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type  ELSE null END ,
			ie_via_aplicacao_w,
			ie_medicacao_paciente_w,
			ie_diluido_w,
			current_setting('adep_gerar_horarios_pck.ie_obs_medico_w')::varchar(5),
			cd_motivo_baixa_w,
			ie_composto_w,
			ie_sol_custo_w, --Le
			ie_alto_risco_w,
			ie_quimio_w,
			cd_unidade_medida_dose_w,
			ie_padronizado_w,
			nr_agrup_prot_w,
			ds_observacao_w,
			ie_oncologia_w,
			ie_regra_disp_w,
			nr_seq_mat_cpoe_w,
			ie_risco_queda_w,
			ie_prescr_anest_w);
		commit;
		end;
	end loop;
	close c01;
	if (ie_lib_pend_rep_p = 'S') then
		begin
		open c02;
		loop
		fetch c02 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_material_w,
				cd_material_w,
				ds_material_w,
				ie_acm_sn_w,
				cd_intervalo_w,
				qt_dose_w,
				nr_agrupamento_w,
				ds_prescricao_w,
				ie_status_w,
				current_setting('adep_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_horario_w,
				ie_via_aplicacao_w,
				ie_medicacao_paciente_w,
				ie_diluido_w,
				current_setting('adep_gerar_horarios_pck.ie_obs_medico_w')::varchar(5),
				cd_motivo_baixa_w,
				ie_composto_w,
				ie_sol_custo_w,
				ie_alto_risco_w,
				ie_quimio_w,
				nr_agrup_prot_w,
				ds_observacao_w,
				ie_oncologia_w,
				ie_regra_disp_w,
				nr_seq_mat_cpoe_w,
				ie_risco_queda_w,
				ie_prescr_anest_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			select	nextval('w_adep_t_seq')
			into STRICT	nr_seq_wadep_w
			;
			if (coalesce(nr_agrup_prot_w::text, '') = '') then
				nr_agrup_prot_w	:= 999;
			end if;
			--Medic
			dt_suspensao_cpoe_w := cpoe_obter_dt_suspensao(nr_seq_mat_cpoe_w, 'M');
			if	dt_suspensao_cpoe_w <= clock_timestamp() then
				ds_material_w := wheb_mensagem_pck.get_texto(820376) || ' ' || substr(ds_material_w,1,240);
			elsif (dt_suspensao_cpoe_w > clock_timestamp()) then
				ds_material_w := '(' || wheb_mensagem_pck.get_texto(1061446, 'DT_SUSPENSAO=' || TO_CHAR(dt_suspensao_cpoe_w, pkg_date_formaters.localize_mask('short', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))) || ') ' || substr(ds_material_w,1,240);
			end if;
			insert into w_adep_t(
				nr_sequencia,
				nm_usuario,
				ie_tipo_item,
				nr_prescricao,
				nr_seq_item,
				cd_item,
				ds_item,
				ie_acm_sn,
				cd_intervalo,
				qt_item,
				nr_agrupamento,
				ds_prescricao,
				ie_status_item,
				nr_seq_proc_interno,
				nr_prescricoes,
				ie_pendente_liberacao,
				ie_horario,
				ie_via_aplicacao,
				ie_medicacao_paciente,
				ie_diluido,
				ie_obs_medico,
				cd_motivo_baixa,
				ie_composto,
				ie_sol_custo,
				ie_alto_risco,
				ie_quimio,
				nr_agrup_prot,
				ds_observacao,
				ie_oncologia,
				ie_regra_disp,
				nr_seq_cpoe,
				ie_risco_queda,
				ie_prescricao_anestesica)
			values (
				nr_seq_wadep_w,
				nm_usuario_p,
				'M',
				current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_material_w,
				cd_material_w,
				substr(ds_material_w,1,240),
				ie_acm_sn_w,
				cd_intervalo_w,
				qt_dose_w,
				nr_agrupamento_w,
				ds_prescricao_w,
				ie_status_w,
				0,
				CASE WHEN ie_acm_sn_w='S' THEN current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type  ELSE null END ,
				current_setting('adep_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_horario_w,
				ie_via_aplicacao_w,
				ie_medicacao_paciente_w,
				ie_diluido_w,
				current_setting('adep_gerar_horarios_pck.ie_obs_medico_w')::varchar(5),
				cd_motivo_baixa_w,
				ie_composto_w,
				ie_sol_custo_w, 
				ie_alto_risco_w,
				ie_quimio_w,
				nr_agrup_prot_w,
				ds_observacao_w,
				ie_oncologia_w,
				ie_regra_disp_w,
				nr_seq_mat_cpoe_w,
				ie_risco_queda_w,
				ie_prescr_anest_w);
			commit;
			end;
		end loop;
		close c02;
		end;
	end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_gerar_horarios_pck.adep_obter_medic ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_lib_pend_rep_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_agrupar_dose_esp_p text, ie_diluicao_acm_sn_gestao_p text, ie_mostrar_substituido_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint, ie_agrupa_suspensos_p text, ie_palm_p text, ie_quimio_p text, ie_clinician_p text, ie_html_p text default 'N') FROM PUBLIC;
