CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_obter_npt_ped ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_sol_realizadas_p text, ie_exibir_sol_suspensas_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_lib_pend_rep_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint, ie_palm_p text) AS $body$
DECLARE

	nr_seq_wadep_w		bigint;
	nr_seq_npt_w		bigint;
	ds_npt_w		varchar(255);
	ie_status_solucao_w	varchar(3);
	ie_lib_pend_rep_w	varchar(1);
	ie_recem_nato_w		varchar(1) := adep_gerar_horarios_pck.get_ie_recem_nato();
	ds_inf_npt_w		varchar(2000);
	nr_seq_npt_cpoe_w	nut_pac.nr_seq_npt_cpoe%type;
	c01 CURSOR FOR
	SELECT	nr_prescricao,
			nr_seq_npt,
			ds_npt,
			ds_inf_npt,
			ie_status_solucao,
			nr_seq_npt_cpoe
	from	(
		SELECT	a.nr_prescricao,
				x.nr_sequencia nr_seq_npt,
				obter_desc_expressao(305336) || ' ' || x.hr_prim_horario ds_npt,
				substr(adep_gerar_horarios_pck.obter_ds_justificativa(obter_justificativa_item(x.nr_prescricao, x.nr_sequencia, 'NPT', x.nr_seq_npt_cpoe)),1,2000) ds_inf_npt,
				substr(obter_status_solucao_prescr(7,a.nr_prescricao,x.nr_sequencia),1,3) ie_status_solucao,
				x.nr_seq_npt_cpoe
		from	nut_pac x,
			prescr_medica a
		where	x.nr_prescricao = a.nr_prescricao
		and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
		and	a.nr_atendimento = nr_atendimento_p
		and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
		and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p, a.ie_lib_farm) = 'S'
		and	x.ie_npt_adulta = 'P'
		and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w		= 'S'))
		and	((x.ie_status in ('I','INT')) or
			 ((Obter_se_acm_sn_agora_especial(null, null, null, null) = 'N') and (x.dt_status between dt_inicial_horarios_p and dt_final_horarios_p)) or
			 ((Obter_se_acm_sn_agora_especial(null, null, null, null) = 'S') and (obter_se_prescr_vig_adep(a.dt_prescricao,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S')))
		and	((ie_exibir_sol_realizadas_p = 'S') or (x.ie_status <> 'T'))
		and	((ie_exibir_sol_suspensas_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
		and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
		and	((ie_regra_inclusao_p = 'S') or
			 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'NPN',
																			cd_estabelecimento_p,
																			cd_setor_usuario_p,
																			cd_perfil_p,
																			null,
																			null,
																			null,
																			null,
																			a.cd_Setor_atendimento,
																			null,
																			null,			-- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
																			null) = 'S'))) -- nr_seq_exame_p
		and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
		and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
		and ((ie_palm_p = 'N') or (x.ie_status = 'N'))
		group by
			a.nr_prescricao,
			x.nr_sequencia,
			x.hr_prim_horario,
			substr(adep_gerar_horarios_pck.obter_ds_justificativa(obter_justificativa_item(x.nr_prescricao, x.nr_sequencia, 'NPT', x.nr_seq_npt_cpoe)),1,2000),
			x.nr_seq_npt_cpoe
		) alias55
	group by
		nr_prescricao,
		nr_seq_npt,
		ds_npt,
		ds_inf_npt,
		ie_status_solucao,
		nr_seq_npt_cpoe;
	
BEGIN
	open c01;
	loop
	fetch c01 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
			nr_seq_npt_w,
			ds_npt_w,
			ds_inf_npt_w,
			ie_status_solucao_w,
			nr_seq_npt_cpoe_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		select	nextval('w_adep_t_seq')
		into STRICT	nr_seq_wadep_w
		;
		insert into w_adep_t(
			nr_sequencia,
			nm_usuario,
			ie_tipo_item,
			nr_prescricao,
			nr_seq_item,
			cd_item,
			ds_item,
			ds_diluicao,
			ie_acm_sn,
			nr_agrupamento,
			nr_seq_proc_interno,
			ie_diferenciado,
			ie_status_item,
			nr_seq_cpoe)
		values (
			nr_seq_wadep_w,
			nm_usuario_p,
			'NPP',
			current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
			nr_seq_npt_w,
			nr_seq_npt_w,
			substr(ds_npt_w,1,240),
			ds_inf_npt_w,
			'N',
			0,
			0,
			'N',
			ie_status_solucao_w,
			nr_seq_npt_cpoe_w);
		commit;
		end;
	end loop;
	close c01;
	CALL Atualizar_adep_controle(nm_usuario_p, nr_atendimento_p, 'NPP', 'N');
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
