            y.ds_npt,
            z.ie_npt_adulta,
            z.ie_forma,           
            z.hr_prim_horario
        
union all

        select	a.nr_prescricao,
			b.nr_seq_dieta_enteral nr_seq_item_ordem,
			b.nr_sequencia nr_seq_ordem,
			c.nr_sequencia nr_seq_horario,
			x.ds_ordem,
			'N' ie_acm_sn,
			z.ds_material ds_prescricao,
