		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			--Recomendacao
			dt_suspensao_cpoe_w := cpoe_obter_dt_suspensao(nr_seq_rec_cpoe_w, 'R');
			if	dt_suspensao_cpoe_w <= clock_timestamp() then
				ds_recomendacao_w := wheb_mensagem_pck.get_texto(820376) || ' ' || substr(ds_recomendacao_w,1,240);
			elsif (dt_suspensao_cpoe_w > clock_timestamp()) then
				ds_recomendacao_w := '(' || wheb_mensagem_pck.get_texto(1061446, 'DT_SUSPENSAO=' || TO_CHAR(dt_suspensao_cpoe_w, pkg_date_formaters.localize_mask('short', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))) || ') ' || substr(ds_recomendacao_w,1,240);
			end if;
			select	nextval('w_adep_t_seq')
			into STRICT	nr_seq_wadep_w
			;
			insert into w_adep_t(
				nr_sequencia,
				nm_usuario,
				ie_tipo_item,
				nr_prescricao,
				nr_seq_item,		
				cd_item,
				ds_item,
				ie_acm_sn,		
				cd_intervalo,
				qt_item,
				ds_prescricao,
				ie_status_item,
				ie_diferenciado,
				nr_agrupamento,
				nr_seq_proc_interno,
				ie_pendente_liberacao,
				ie_horario,
				ds_diluicao,
				ie_oncologia,
				nr_seq_apres_inf,
				nr_seq_cpoe)
			values (
				nr_seq_wadep_w,
				nm_usuario_p,
				'R',
				current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_recomendacao_w,
				cd_recomendacao_w,
				substr(ds_recomendacao_w,1,240),
				ie_acm_sn_w,
				cd_intervalo_w,
				qt_recomendacao_w,
				ds_intervalo_w,
				ie_status_w,
				'N',
				0,
				0,
				current_setting('adep_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_horario_w,
				ds_diluicao_w,
				ie_oncologia_w,
				coalesce(nr_seq_apres_w,999),
				nr_seq_rec_cpoe_w);
			commit;
			end;
		end loop;
		close c02;	
		end;
	end if;
	END;

